import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const AdminTailorDetails = () => {
    const {id} = useParams();
  const [user, setUser] = useState(null);
  const apiUrl = `http://localhost:5000/admin/users/${id}`;

  useEffect(()=>{
    const getUserDetails = async() => {
        await axios.get(apiUrl).then(response => {
            setUser(response.data.user);
        }).catch(error => {
            console.error(error);
        });
    }

    return ()=>{
        getUserDetails()
    }
  }, [apiUrl]);
  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="utilities">
              <Link to={`/admin/edit-user/${user.id}`} className="edit-btn">
                Edit
              </Link>
              <form
                action="/admin/<%= user.id %>?_method=DELETE"
                method="POST"
                className="d-inline"
              >
                <button type="submit" className="delete-btn">
                  Delete
                </button>
              </form>
            </div>
            <h1 className="title">User Details {user.name}</h1>
            <div className="row">
              <span>
                <strong>ID</strong>
                <p>{user.id}</p>
              </span>
              <span>
                <strong>Name</strong>
                <p>{user.name}</p>
              </span>
              <span>
                <strong>Surname</strong>
                <p>{user.surname}</p>
              </span>
              <span>
                <strong>Role</strong>
                <p>{user.role}</p>
              </span>
              <span>
                <strong>Language</strong>
                <p>
                  {user.language === "en" && "English"}
                  {user.language === "ur" && "Urdu"}
                  {user.language === "ar" && "Arabic"}
                  {user.language === "hi" && "Hindi"}
                </p>
              </span>
              <span>
                <strong>Date Created</strong>
                <p>
                  {new Date(user.date).toLocaleDateString("en-us", {
                    month: "long",
                    year: "numeric",
                    day: "numeric",
                  })}
                </p>
              </span>
            </div>
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
