import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";

const MyOrders = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
    const authKey = localStorage.getItem("jwt");
    const [orders, setOrders] = useState([]);

  useEffect(()=>{
    const isMounted = true;
    document.title="";

    const fetchData = async () => {
        await axios
          .get(`${serverUrl}/admin/my-orders/${id}`, {
            headers: { Authorization: `Bearer ${authKey}` },
          })
          .then((response) => {
            if (isMounted) {
              setOrders(response.data.available_fabric);
              setAverageServiceTime(response.data.average_service_time);
              setCustomers(response.data.customers);
              setInvoiceData(response.data.invoice_data);
              setTotalRevenue(response.data.total_revenue);
              setDisplay(true);
              setIsLoading(false);
            }
          })
          .catch((error) => {
            if (isMounted) {
              setDisplay(false);
              setIsLoading(true);
              if (error.response.status === 403) {
                toast.warn("You are not authorized to access that page");
                navigate("/");
              } else {
                toast.error(error.response?.data?.message || error.message);
              }
              return;
            }
          });
      };
  
      fetchData();

      return () => {
        isMounted = false;
      };
  },[])
  return <div>MyOrders - {id}</div>;
};

export default MyOrders;
