import React, { useState } from 'react'
import Navbar from '../../components/Navbar'
import { Link } from 'react-router-dom';
import Footer from '../../components/Footer';

const Products = () => {
    const [writingDirection, setWritingDirection] = useState("ltr");
    const [message, setMessage] = useState("");
    const [products, setProducts] = useState([]);
  return (
    <><Navbar /><main>
          <section id="inventory" class="khamco-section" dir={writingDirection}>
              <div class="utilities">
                  <Link to="/products/new">Add New Product <i class="fas fa-cart-plus"></i></Link>
                  <p class="message">
                      {message}
                  </p>
              </div>
              <div class="utilities">
                  <form action="/products" method="GET" class="search-engine">
                      <input type="text" placeholder="Search product name" name="name" value="<%= searchOptions.name %>"/>
                          <button type="submit"><i class="fas fa-search"></i></button>
                      </form>
                  <form action="/products" method="GET" class="search-engine" id="date-form">
                      <input type="date" name="date" id="date-search" value="<%= searchOptions.date %>"/>
                          <button type="submit"><i class="fas fa-check"></i></button>
                      </form>
              </div>
              <h1 class="title">All Products</h1>
                  { products.length > 0 ? 
                    products.map((product, index) => (
                        <div class="flex-wrapper" dir="auto" key={index}>
                          <img src={`${product.photoPath}`} alt="The associated product" loading="lazy"/>
                              <p><strong>Name</strong>{product.name}
                              </p>
                              <p><strong>Category</strong>{product.category}
                              </p>
                              <Link to={`/products/${product.id}`} class="edit-btn">View</Link>
                          </div>
                    )): (
                    <p class="empty">No product data.</p>
                  )}
          </section>
      </main>
        <Footer/>
      </>
  )
}

export default Products
