import { useState } from "react";
import { useStoreActions } from "easy-peasy";

const Clothing = ({ label, imageUrl, clothingOptions, setClothingOptions }) => {
  const [isChecked, setIsChecked] = useState(false);
  // Generate a unique ID for each Clothing component
  const inputId = `clothing_item_${label.replace(/\s+/g, "_").toLowerCase()}`;
  const addItemCount = useStoreActions((actions) => actions.addItemCount);
  const remove = useStoreActions((actions) => actions.removeItem);

  const add = () => {
    if (isChecked) {
      remove();
      // setClothingOptions(() => {
      //   clothingOptions.splice(clothingOptions.indexOf(label), 1);
      // });
      console.logclothingOptions.indexOf(label)
    } else {
      addItemCount();
      const removeDuplicates = new Set([...clothingOptions, label]); 
      setClothingOptions([...removeDuplicates]);
      console.log(clothingOptions);
    }
  };
  return (
    <>
      <label
        htmlFor={inputId}
        className={`w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 ${
          isChecked ? "border-2 border-khamco-primary bg-[#ccc]" : ""
        }`}
        onClick={add}
      >
        <img src={imageUrl} alt={label} />
        <p className="text-sm">{label}</p>
        <input
          type="checkbox"
          id={inputId}
          onChange={() => {
            setIsChecked(!isChecked);
          }}
          className="accent-khamco-primary"
        />
      </label>
    </>
  );
};

export default Clothing;
