import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";

const AdminTailors = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/tailors"
    : "https://khamco-shop-backend.onrender.com/admin/tailors";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "Admin Tailor Data - Khamco Shop";

    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setUsers(response.data.users);
          setDisplay(true);
          setIsLoading(false);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response?.data?.message || error.message);
          }
          return;
        });
    };

    fetchData();
  }, [apiUrl, authKey, navigate]);

  const deleteUser = async (userId) => {
    setLoading(true);
    try {
      const response = await axios.delete(`${apiUrl}/${userId}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      setUsers(response.data.users);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <AdminPanel />
        <section
          id="users-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <h1 className="text-center text-[2rem] font-minimoBold ">All Tailors ({users.length || 0})</h1>
          <div className="utilities">
            <Link to="/users/register-user">
              Add New Tailor <i className="fas fa-user-plus"></i>
            </Link>
          </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] animate-pulse"
                ></div>
              ))}
          {display && (
            <>
              {users && users.length > 0 ? (
                users.map((user, index) => (
                  <div
                    className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                    key={index}
                  >
                    <p>
                      <strong>Name</strong>: {user.name}
                    </p>
                    <p className="capitalize">
                      <strong>Role</strong>: {user.role}
                    </p>
                    <p>
                      <strong>Email</strong>:{" "}
                      <a href={`mailto:${user.email}`}>{user.email}</a>
                    </p>
                    <Link to={`/admin/users/${user._id}`} className="edit-btn">
                      View
                    </Link>
                    <button
                      className="delete-btn"
                      disabled={loading}
                      onClick={() => deleteUser(user._id)}
                    >
                      {loading ? (
                        <i className="fas fa-spinner animate-spin"></i>
                      ) : (
                        <i className="fas fa-trash"></i>
                      )}
                    </button>
                  </div>
                ))
              ) : (
                <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                  No users in the database
                </p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailors;
