import React from "react";

const ServiceForm = ({
  setService,
  setPrice,
  setDate,
  loading,
  service,
  price,
  date,
}) => {
  // Function to format the date to YYYY-MM-DD
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  return (
    <>
      <div className="form-row">
        <label className="text-lg my-2 select-none self-start font-medium" htmlFor="service">Service</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          onChange={(e) => setService(e.target.value)}
          value={service || ""}
          placeholder="Service name"
        />
      </div>
      <div className="form-row">
        <label className="text-lg my-2 select-none self-start font-medium" htmlFor="price">Price</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="number"
          step="0.01"
          onChange={(e) => setPrice(e.target.value)}
          value={price || ""}
          placeholder="Price"
        />
      </div>
      <div className="form-row">
        <label className="text-lg my-2 select-none self-start font-medium" htmlFor="date">Date</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="date"
          onChange={(e) =>
            setDate(new Date(e.target.value).toISOString().split("T")[0])
          }
          value={date ? formatDate(date) : ""}
        />
      </div>
      <div className="form-row">
        <button type="submit" className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer">
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export default ServiceForm;
