import { createStore, action } from 'easy-peasy';
import axios from "axios";

const store = createStore({
  authKey: localStorage.getItem("jwt"),
  itemCount: [],
  addItemCount: action((state) => {
    state.itemCount.push(state.itemCount.length + 1);
  }),
  removeItem: action((state) => {
    state.itemCount.pop();
  }),
  userLanguage: null,
  selectUserLanguage: action(async(state, payload) => {
    state.userLanguage = payload;
    await axios
      .post(
        apiUrl,
        { language: payload },
        {
          headers: { Authorization: `Bearer ${state.authKey}` },
        }
      )
      .catch((error) => {
        alert(error.message);
      });
  })
});

export default store;
