import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";

const OrderDetails = () => {
  const { id } = useParams();
  const [openComment, setOpenComment] = useState(false);
  const [order, setOrder] = useState({});
  const [productDetails, setProductDetails] = useState(null);
  const [serviceDetails, setServiceDetails] = useState(null);
  const [invoice, setInvoice] = useState({});
  const [customer, setCustomer] = useState({});
  const [activeUser, setActiveUser] = useState(null);
  const [productItems, setProductItems] = useState([]);
  const [serviceItems, setServiceItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [comments, setComments] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const orderRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Details - Khamco Shop";

    const fetchOrder = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setLoading(false);
          setOrder(response.data.order);
          setProductDetails(
            response.data.order.details.filter(
              (item) => item.type === "product"
            )
          );
          setServiceDetails(
            response.data.order.details.filter(
              (item) => item.type === "service"
            )
          );
          setProductItems(
            response.data.order.details.filter(
              (item) => item.type === "product"
            )
          );
          setServiceItems(
            response.data.order.details.filter(
              (item) => item.type === "service"
            )
          );
          setInvoice(response.data.invoice);
          setCustomer(response.data.customer);
          setActiveUser(response.data.active_user);
        }
      } catch (error) {
        if (isMounted) {
          if (error.response) {
            navigate("/");
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchOrder();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate]);

  const deleteEntireOrder = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/orders");
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to do that");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const submitComment = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios
      .put(
        `${apiUrl}/orders/comments/${order._id}`,
        {
          comments: comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setLoading(false);
        setOrder(response.data.order);
        setProductDetails(
          response.data.order.details.filter((item) => item.type === "product")
        );
        setServiceDetails(
          response.data.order.details.filter((item) => item.type === "service")
        );
        setOpenComment(false);
      })
      .catch((error) => {
        console.error(error);
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  const handlePrint = () => {
    const content = orderRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Customer Details</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            span:not(div span){
              padding: 0.25rem;
              text-lg: 1.125rem;
              border-radius: 0.75rem;
              background-color: #ccc;
            }
            div {
              display: flex;
              flex-direction: column;
              gap-: 1rem; 
              margin: 2rem 0;
            }
            
            span {
              display: inline-flex;
              justify-content: start;
              align-items: center;
              gap: 2rem;
              text-wrap: balance;
              margin: 1rem 0;
            }
            table {
              width: 100%; 
              margin: auto;
              margin: 2rem 0; 
              border-collapse: collapse;
              font-size: 1.2rem; 
              overflow: auto;
            }
            caption {
              font-weight: bold;
              margin-bottom: 1rem;
              color: #ed1965;
            } 
            thead {
              color: #ed1965;
              font-weight: bold; 
              background-color: #ddd];
            }
            tr {
              border-bottom-width: 2px;
              border-right-width: 2px;
              border-left-width: 2px;
              position: relative;
              border: 1px solid #00f; 
              }
            td, th {
              border: 1px solid #00f; 
              text-align: center;
              padding: 0.5rem;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> All Orders
            </Link>
          </div>
          {loading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(6).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[1.8rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-2/4 bg-[#666] animate-pulse m-auto h-[100vh] my-[2rem]"></div>
                </div>
              </div>
            </>
          )}
          {order && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={handlePrint}
                >
                  Print <i className="fas fa-print"></i>
                </button>
                <form onSubmit={deleteEntireOrder}>
                  <button
                    type="submit"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  >
                    Delete <i className="fas fa-times"></i>
                  </button>
                </form>
                <button
                  type="submit"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[green] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => setOpenComment(true)}
                >
                  Add Comment <i className="fas fa-message"></i>
                </button>
              </div>
              <div ref={orderRef}>
                <h1 className="text-center text-[2rem] font-minimoBold flex items-center justify-center gap-4">
                  Order Details
                </h1>
                <div className="flex flex-col gap-[1rem] my-[2rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Measurements Taken By:</strong>
                    <p>{order.measurer || "--"}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Assigned By:</strong>
                    <p>{order.assigned_by || "--"}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order Status:</strong>
                    <p>{order.order_status || "--"}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order No:</strong>
                    <p>{order.uniqueId}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Date Measurements Taken:</strong>
                    <p>
                      {order.measuring_date
                        ? new Date(order.measuring_date).toLocaleDateString(
                            "en-us",
                            { month: "long", year: "numeric", day: "numeric" }
                          )
                        : "--"}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Customer ID No:</strong>
                    <p
                    >
                      {order.customer_id}
                      {order?.customer_status === "VIP" ? <i className="fas fa-star text-[gold] animate-pulse"></i> : ""}
                    </p>
                  </span>
                  {invoice ? (
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong>Invoice No:</strong>
                      <p
                      >
                        {order.uniqueId}{" "}
                        <i className="fas fa-external-link"></i>
                      </p>
                    </span>
                  ) : (
                    <span className="text-[red]">No invoice found</span>
                  )}
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Total Fabric Quantity:</strong>
                    <p>
                      {productItems &&
                        productItems
                          .reduce((prev, curr) => prev + curr.quantity, 0)
                          .toFixed(2)}{" "}
                      m
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Tailoring Service Quantity:</strong>
                    <p>
                      {serviceItems &&
                        serviceItems.reduce(
                          (prev, curr) => prev + curr.quantity,
                          0
                        )}{" "}
                      x pieces
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Garment Types:</strong>
                    <p>
                      {order.details &&
                        order.details.map(
                          (item) => `${item.quantity} x ${item.name}, `
                        )}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Number of Dependents:</strong>
                    <p>{order.dependents ? order.dependents.length : 0}</p>
                  </span>
                  {productDetails && productDetails.length > 0 ? (
                    <>
                      <table className="w-full m-auto my-8 border-collapse text-[1.2rem] overflow-auto">
                        <caption className="text-khamco-primary font-bold mb-4">
                          Fabric Information
                        </caption>
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">No</th>
                            <th className="text-center p-2">Fabric Name</th>
                            <th className="text-center p-2">Quantity</th>
                            <th className="text-center p-2">Measurements</th>
                          </tr>
                        </thead>
                        <tbody>
                          {productDetails.map((item, index) => (
                            <tr
                              key={index}
                              className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                            >
                              <td className="text-center p-2">{index + 1}</td>
                              <td className="text-center p-2">{item.name}</td>
                              <td className="text-center p-2">
                                {item.quantity.toFixed(2)} meters
                              </td>
                              <td className="text-center p-2">
                                <Link
                                  className="text-[blue] no-underline hover:underline cursor-pointer"
                                  to={`/customers/${customer._id}/measurements`}
                                  target="_blank"
                                >
                                  Measurements{" "}
                                  <i className="fas fa-external-link"></i>
                                </Link>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                      No products for this order
                    </p>
                  )}
                  {serviceDetails && serviceDetails.length > 0 ? (
                    <>
                      <table className="w-full m-auto my-8 border-collapse text-[1.2rem] overflow-auto">
                        <caption className="text-khamco-primary font-bold mb-4">
                          Service Information
                        </caption>
                        <thead className="text-khamco-primary font-bold bg-[#ddd]">
                          <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                            <th className="text-center p-2">No</th>
                            <th className="text-center p-2">Quantity</th>
                            <th className="text-center p-2">
                              Tailoring Service
                            </th>
                            <th className="text-center p-2">Measurements</th>
                          </tr>
                        </thead>
                        <tbody>
                          {serviceDetails.map((item, index) => (
                            <tr
                              key={index}
                              className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                            >
                              <td className="text-center p-2">{index + 1}</td>
                              <td className="text-center p-2">
                                {item.quantity}
                              </td>
                              <td className="text-center p-2">{item.name}</td>
                              <td className="text-center p-2">
                                <Link
                                  className="text-[blue] no-underline hover:underline cursor-pointer"
                                  to={`/customers/${customer._id}/measurements`}
                                  target="_blank"
                                >
                                  Measurements{" "}
                                  <i className="fas fa-external-link"></i>
                                </Link>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </>
                  ) : (
                    <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                      No services for this order
                    </p>
                  )}
                  <span className="flex flex-col justify-start gap-[2rem] my-[2rem] w-full">
                    <strong className="underline w-full">
                      Comments/Feedback:
                    </strong>
                    <div className="flex flex-col gap-[1rem]">
                      {order.comments &&
                        order.comments.map((comment) => (
                          <div className="bg-[#ddd] text-[1rem] p-[0.5rem] rounded-lg flex flex-col gap-2">
                            <span className="self-start text-khamco-secondary italic">
                              {comment.user}
                            </span>
                            <span>{comment.text}</span>
                            <span className="self-end">
                              <strong>
                                {new Date(comment.timestamp).toLocaleDateString(
                                  "en-us",
                                  {
                                    day: "numeric",
                                    month: "long",
                                    year: "numeric",
                                  }
                                )}{", "}
                                {new Date(comment.timestamp).toLocaleTimeString(
                                  "en-us",
                                  {
                                    hour: "numeric",
                                    minute: "numeric",
                                  }
                                )}
                              </strong>
                            </span>
                          </div>
                        ))}
                    </div>
                  </span>
                  <address className="bg-[#ddd] font-medium p-[0.5rem] rounded-lg">
                    Khamco Company, LLC - PO Box 21353, Safat 13074, Qiba,
                    Kuwait (+965) 22400650 - (+965) 55627017 - info@kham.co -
                    @khamco.kw
                  </address>
                </div>
              </div>
            </div>
          )}
        </section>
        <dialog
          className="fixed z-50 top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-screen md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openComment}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={submitComment}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenComment(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Add a Comment
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Comments
                </label>
                <textarea
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  placeholder="Write a comment"
                  onChange={(e) => setComments(e.target.value)}
                ></textarea>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Update
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default OrderDetails;
