import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import { useStoreState } from "easy-peasy";

const CustomerInvoices = () => {
  const [invoices, setInvoices] = useState(null);
  const [customer, setCustomer] = useState(null);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  return (
    <>
      <Navbar />
      <main>
        <section
          id="customer-page"
          className="khamco-section"
          dir={writingDirection}
        >
          <div className="utilities">
            <Link to={`/customers/${customer._id}`}>
              <i className="fas fa-arrow-left"></i>
              Customer {`${customer.name} ${customer.surname}`}
            </Link>
          </div>
          <div className="utilities">
            <form
              action="/customers/<%= customer.id %>/invoices"
              method="GET"
              className="search-engine"
              id="date-form"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                value="<%= searchOptions.date %>"
              />
              <button type="submit">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="title">{`All Invoices for ${customer.name} ${customer.surname}`}</h1>
          {invoices && invoices.length > 0 ? (
            invoices.map((invoice, index) => (
              <div className="flex-wrapper" key={index}>
                <p>
                  <strong>Name</strong>:{" "}
                  {new Date(invoice.date).toLocaleDateString("en-US", {
                    month: "long",
                    day: "numeric",
                    year: "numeric",
                  })}
                </p>
                <p>
                  <strong>Payment Status</strong>
                  {invoice.payment_status}
                </p>
                <p><strong>Membership</strong>
                    {customer.membership_status === "VIP" ?
                            `VIP ${<i className="fas fa-star blink"></i>}`
                        :
                        "Basic"
                    }
                </p>
                <Link to="/invoices/<%=invoice.id%>" className="edit-btn">
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="empty">This customer has no invoices.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CustomerInvoices;
