import React, { useEffect, useState } from "react";
import Footer from "../../../components/Footer";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";
import CustomerForm from "../CustomerForm";
import { areaNames } from "../../../utilities";
import { useTranslation } from "react-i18next";
import { apiUrl, authKey } from "../../../utilities";
import MeasurementsForm from "../measurements/MeasurementsForm";

const EditDependent = () => {
  const { id } = useParams();
  const { dependentId } = useParams();
  const [customer, setCustomer] = useState();
  const [dependent, setDependent] = useState();
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("");
  const [area, setArea] = useState("");
  const [country, setCountry] = useState("Kuwait");
  const [militaryStatus, setMilitaryStatus] = useState("");
  const [membershipStatus, setMembershipStatus] = useState("");
  const [militaryNumber, setMilitaryNumber] = useState("");
  const [militaryRank, setMilitaryRank] = useState("");
  const [militaryDivision, setMilitaryDivision] = useState("");
  const [date, setDate] = useState(null);
  const [areas, setAreas] = useState(areaNames);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [shirtType, setShirtType] = useState("");
  const [specialOrder, setSpecialOrder] = useState("");
  const [alterations, setAlterations] = useState("");
  const [notes, setNotes] = useState("");
  const [measurementValues, setMeasurementValues] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/customers/edit/${id}/dependents/${dependentId}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setDependent(response.data.dependent);
          setMeasurementValues(
            response.data.dependent.measurements.find(
              (measurement) => measurement._id === dependentId
            )
          );
          setLoading(false);
        })
        .catch((error) => {
          setLoading(false);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        });
    };

    return () => {
      fetchData();
    };
  }, [dependentId, id, navigate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/customers/${id}/dependents/${dependentId}`,
        {
          name: customerName,
          surname: customerSurname,
          email: customerEmail,
          phone: customerPhone,
          house_unit: houseUnit,
          governorate: governorate,
          area: area,
          country: country,
          military_status: militaryStatus,
          membership_status: membershipStatus,
          military_idnumber: militaryNumber,
          military_rank: militaryRank,
          military_department: militaryDivision,
          date_created: date,
          clothing_options: clothingOptions,
          ...measurementValues,
          description: description,
          special_order: specialOrder,
          alterations: alterations,
          notes: notes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      navigate(
        `/customers/${response.data.customer._id}/dependents/${response.data.dependent._id}`
      );
      setLoading(false);
    } catch (error) {
      setLoading(false);
      if (error.response) {
        const { status, data } = error.response;

        if (status === 404) {
          if (data.message === "Customer not found") {
            toast.error("Customer not found");
          } else if (data.message === "Dependent not found") {
            toast.error("Dependent not found");
          } else if (data.message === "Date is required") {
            toast.error("The date field is required");
          } else {
            toast.error("Resource not found");
          }
        } else if (status === 400) {
          toast.error("Invalid ObjectId format");
        } else {
          toast.error("An error occurred while updating dependent details");
        }
      } else {
        toast.error("An error occurred while updating dependent details");
      }
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="p-[4rem] grid justify-center items-center">
          {customer && (
            <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
              <Link
                to={`/customers/${customer._id}`}
                className="text-[blue] no-underline hover:underline cursor-pointer"
              >
                <i className="fas fa-arrow-left"></i>{" "}
                {`Customer ${customer.name} ${customer.surname}`}
              </Link>
            </div>
          )}
          <h1 className="text-center text-[2rem] font-minimoBold">
            Edit Customer Details
          </h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <CustomerForm
              isCreateCustomer={false}
                  membershipStatus={membershipStatus}
                  setCustomerName={setCustomerName}
                  setCustomerSurname={setCustomerSurname}
                  setCustomerEmail={setCustomerEmail}
                  setCustomerPhone={setCustomerPhone}
                  setHouseUnit={setHouseUnit}
                  governorate={governorate}
                  setGovernorate={setGovernorate}
                  area={area}
                  setArea={setArea}
                  setCountry={setCountry}
                  militaryStatus={militaryStatus}
                  militaryNumber={militaryNumber}
                  militaryRank={militaryRank}
                  setMilitaryStatus={setMilitaryStatus}
                  setMilitaryNumber={setMilitaryNumber}
                  setMilitaryRank={setMilitaryRank}
                  setMilitaryDivision={setMilitaryDivision}
                  setMembershipStatus={setMembershipStatus}
                  date={date}
                  setDate={setDate}
                  areas={areas}
                  setAreas={setAreas}
                  loading={loading}
                  customerName={customerName}
                  customerSurname={customerSurname}
                  customerEmail={customerEmail}
                  customerPhone={customerPhone}
                  houseUnit={houseUnit}
            />
            <MeasurementsForm
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              shirtType={shirtType}
              setShirtType={setShirtType}
              setDescription={setDescription}
              loading={loading}
              setSpecialOrder={setSpecialOrder}
              setAlterations={setAlterations}
              setNotes={setNotes}
              setValues={setMeasurementValues}
              values={measurementValues}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditDependent;
