import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useParams } from "react-router-dom";

const Quiz = () => {
  const { category } = useParams();
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:8000"
    : "";
  const [gameStarted, setGameStarted] = useState(false);
  const [displayResult, setDisplayResult] = useState(true);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [nextButton, setNextButton] = useState(false);
  const [answerScore, setAnswerScore] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/${category}`)
        .then((response) => {
          setQuestions(response.data.questions);
        })
        .catch((error) => {
          console.error("Error: ", error);
          toast.error(error.response.data?.message || error.message);
        })
        .finally(() => {
          setIsLoading(false);
        });
    };

    fetchData();
  }, [apiUrl, category]);

  const setNextQuestion = () => {
    setCurrentQuestionIndex((prev) => prev + 1);
    setNextButton(false);
  };

  const startGame = () => {
    setGameStarted(true); // Hides the start button
    setDisplayResult(false); // Hides the quiz score
    setNextButton(true); // Shows the next button
    setCurrentQuestionIndex(0);
  };

  const selectAnswer = (correct, questionLength) => {
    setNextQuestion();
    if (correct) {
      setAnswerScore((prev) => prev + 1);
    }

    if (currentQuestionIndex === questionLength - 1) {
      setDisplayResult(true);
    }
  };

  return (
    <div className="absolute left-2/4 top-2/4 -translate-x-2/4 -translate-y-2/4 w-[90%] max-w-[90%] md:w-[800px] md:max-w-[80%] bg-[white] rounded-[5px] p-[10px] shadow-md">
      {gameStarted && (
        <div id="question-container">
          {questions.length > 0 && (
            <>
              <div id="question">
                <h1>{questions[0].quiz[currentQuestionIndex]?.question}</h1>
              </div>
              <br />
              <div className="question-picture">
                <img
                  className="w-[300px] h-[200px]"
                  alt={questions[0].quiz[currentQuestionIndex]?.category}
                  src={questions[0].quiz[currentQuestionIndex]?.picture}
                />
              </div>
              <div
                id="answer-buttons"
                className="grid grid-cols-1 md:grid-cols-2 gap-[10px] my-[20px] mx-0"
              >
                {questions[0].quiz[currentQuestionIndex]?.answers?.map(
                  (answer, index) => (
                    <button
                      className="btn"
                      key={index}
                      onClick={() =>
                        selectAnswer(answer.correct, questions.length)
                      }
                    >
                      {answer.text}
                    </button>
                  )
                )}
              </div>
            </>
          )}
          <div className={`result ${displayResult ? "block" : "hidden"}`}>
            <h4>{`Your score: ${answerScore}`}</h4>
          </div>
        </div>
      )}
      {!gameStarted && (
        <div className="flex flex-col items-center gap-[1rem] my-[2rem]">
          <h1 className="text-[#440066] font-bold text-[3rem] underline">
            {questions[0]?.category} Quiz
          </h1>
          <p>Press "start" when you are ready</p>
        </div>
      )}
      <div className="flex justify-center items-center">
        {!gameStarted && (
          <button
            onClick={startGame}
            className="text-[1.5rem] font-bold py-[10px] px-[20px] cursor-pointer btn"
          >
            Start
          </button>
        )}
        {nextButton && (
          <button
            id="next-btn"
            className="text-[1.5rem] font-bold py-[10px] px-[20px] cursor-pointer btn"
            onClick={setNextButton}
          >
            Next
          </button>
        )}
      </div>
    </div>
  );
};

export default Quiz;
