import { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import logo from "../../assets/images/logo.jpg";
import { useTranslation } from "react-i18next";
import html2canvas from "html2canvas";
import jsPDF from "jspdf";

const ShowInvoice = () => {
  const { id } = useParams();
  const [dialogState, setDialogState] = useState(false);
  const [order, setOrder] = useState([]);
  const [invoice, setInvoice] = useState();
  const [customer, setCustomer] = useState(null);
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const invoiceRef = useRef(null);
  const { t, i18n } = useTranslation();
  const [pending, setPending] = useState(false);

  useEffect(() => {
    let isMounted = true;

    document.title = "Invoice Details - Khamco Shop";

    const fetchInvoice = async () => {
      try {
        const response = await axios.get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setInvoice(response.data.invoice);
          setLoading(false);
          setOrder(response.data.order);
          setCustomer(response.data.customer);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(true);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchInvoice();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate]);

  const deleteInvoice = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/${invoice._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/invoices");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/${id}`,
        {
          payment_type: paymentType,
          amount_paid: amountPaid,
          payment_status: paymentStatus,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setCustomer(response.data.customer);
      setOrder(response.data.order);
      toast.success(response.data.message);
      setDialogState(false);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = invoiceRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Invoice</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-bottom: 2.5rem;
            }
            table, th, td {
              border: 4px solid blue;
            }
            th, td {
              padding: 10px;
              text-align: left;
            }
            .invoice-data{
              display: flex;
              flex-wrap;
              gap: 2rem;
              align-items: center;
              margin: 1rem 0;
              padding: 0 0.2rem; 
            }
            .invoice-data strong {
              color: blue;
              font-size: 1.5rem;
            }
            .invoice-data input {
              border: 0.2rem solid black;
              padding: 0.2rem;
              font-size: 1.25rem;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  // Function to handle WhatsApp sharing
  const handleWhatsAppShare = async () => {
    setPending(true);
    try {
      // Determine the scale based on device pixel ratio
      const scale = window.devicePixelRatio || 1;

      // Convert invoice content to a high-quality canvas
      const canvas = await html2canvas(invoiceRef.current, {
        scale: scale, // Use device pixel ratio to scale
      });

      // Get the image data from the canvas
      const imgData = canvas.toDataURL("image/png");

      // Get the device width (in mm)
      const deviceWidthInMM = window.innerWidth * 0.264583; // Convert px to mm

      // Calculate width and height for PDF
      const pdfWidth = deviceWidthInMM < 210 ? deviceWidthInMM : 210; // Cap the width at A4 size (210mm)
      const imgHeight = (canvas.height * pdfWidth) / canvas.width; // Maintain aspect ratio

      // Create a jsPDF instance
      const pdf = new jsPDF({
        orientation: "portrait", // or "landscape" depending on the content
        unit: "mm",
        format: [pdfWidth, imgHeight], // Dynamically sized PDF
      });

      // Add the image to the PDF
      pdf.addImage(imgData, "PNG", 0, 0, pdfWidth, imgHeight);

      // Convert the PDF to a Blob
      const pdfBlob = pdf.output("blob");

      // Upload the PDF to the server (which will upload to Google Drive)
      const formData = new FormData();
      const firstName = customer.name;
      const lastName = customer.surname;
      formData.append("file", pdfBlob, `${firstName}-${lastName}-invoice.pdf`);

      const response = await fetch(`${apiUrl}/upload`, {
        method: "POST",
        body: formData,
      });

      // Get the uploaded file URL from Google Drive
      const { fileUrl } = await response.json();

      if (fileUrl) {
        setPending(false);
      }

      // Encode the URL for WhatsApp sharing
      const whatsappMessage = `Check your invoice: ${fileUrl}`;
      const encodedMessage = encodeURIComponent(whatsappMessage);

      // Open WhatsApp with the URL and message
      window.open(
        `https://api.whatsapp.com/send?text=${encodedMessage}`,
        "_blank"
      );
    } catch (error) {
      setPending(false);
      console.error("Error generating PDF or sharing: ", error);
      toast.error(error?.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"> </i> {t("Invoices")}
            </Link>
          </div>
          {loading && (
            <>
              <table className="text-center">
                <thead>
                  <tr>
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {invoice && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <form onSubmit={deleteInvoice}>
                  <button
                    type="submit"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                    title="Delete this invoice"
                  >
                    {t("Delete")}
                  </button>
                </form>
                <button
                  title="Share to Whatsapp"
                  className="bg-[limegreen] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer"
                  onClick={handleWhatsAppShare}
                >
                  {pending ? (
                    <i className="fas fa-spinner animate-spin fa-2x"></i>
                  ) : (
                    <i className="fab fa-whatsapp fa-2x"></i>
                  )}
                </button>
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={handlePrint}
                >
                  {t("Print")} <i className="fas fa-print"></i>
                </button>
                {order ? (
                  <Link
                    to={`/orders/${order._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    {t("View Order")}
                  </Link>
                ) : (
                  <p className="message">No Orders</p>
                )}
                <button
                  className="bg-[gray] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:bg-[goldenrod]"
                  onClick={() => setDialogState(true)}
                >
                  {t("Change Payment Details")}: {invoice.payment_status}{" "}
                  <i className="fas fa-money-check"></i>
                </button>
              </div>
              <div
                className="w-full m-auto shadow-md shadow-[#666] p-[1rem]"
                ref={invoiceRef}
              >
                <div className="bg-white m-auto px-0">
                  <div>
                    <span className="w-full flex justify-evenly items-center m-auto">
                      <img src={logo} alt="Logo" />
                    </span>
                  </div>
                  <div>
                    <table className="border-collapse w-full my-[1rem] m-auto text-[0.9rem] md:text-[1.2rem]">
                      <thead className="text-khamco-primary font-bold">
                        <tr className="border-[0.2rem] border-khamco-secondary border-t-0">
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Item")}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Price")}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Quantity")}
                          </td>
                          <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                            {t("Total")}
                          </td>
                        </tr>
                      </thead>
                      <tbody>
                        {invoice.details &&
                          invoice.details.map((item, index) => (
                            <tr
                              className="border-[0.2rem] border-khamco-secondary border-t-0"
                              key={index}
                            >
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.name || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.price || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.quantity || "--"}
                              </td>
                              <td className="text-center border-r-2 border-khamco-secondary">
                                {item.quantity * item.price}
                              </td>
                            </tr>
                          ))}
                      </tbody>
                    </table>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Total Price")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.total_price
                        )}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Amount Paid")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_paid
                        )}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Amount Remaining")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`KWD ${new Intl.NumberFormat("en-US").format(
                          invoice.amount_remaining
                        )}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className=" text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Date")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${new Date(invoice.date).toLocaleDateString("en-us", {
                          month: "long",
                          day: "2-digit",
                          year: "numeric",
                        })}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className=" text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Invoice")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${invoice.uniqueId}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Payment Type")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${invoice.payment_type}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Customer")} ID
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${customer && customer.uniqueId}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Customer Name")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${
                          customer && `${customer.name} ${customer.surname}`
                        }`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Phone Number")}
                      </strong>
                      <span className="border-[0.2rem] border-black p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${
                          invoice.customer_phone &&
                          !invoice.customer_phone.startsWith("+965")
                            ? "+965" + invoice.customer_phone
                            : invoice.customer_phone
                        }`}
                      </span>
                    </div>
                    <div className="flex justify-center flex-wrap items-center gap-[2rem] py-[2rem]">
                      <span className="text-lg flex items-center gap-[1rem]">
                        <i className="fas fa-phone-volume text-4xl"></i>
                        <p>
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="tel:+96522400650"
                          >
                            (+965) 22400650
                          </a>{" "}
                          -{" "}
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="tel:+96555627017"
                          >
                            (+965) 55627017
                          </a>
                        </p>
                      </span>
                      <span className="text-lg flex items-center gap-[1rem]">
                        <i className="fas fa-envelope text-4xl"></i>
                        <p>
                          <a
                            className="text-[blue] no-underline hover:underline cursor-pointer"
                            href="mailto:info@kham.co"
                          >
                            info@kham.co
                          </a>{" "}
                          - @khamco.kw
                        </p>
                      </span>
                      <address className="text-[#666] mx-[0.5rem]">
                        Khamco Company, LLC - PO Box 21353, Safat 13074, Qibla,
                        Kuwait
                      </address>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </section>
        <dialog
          className="fixed w-[90%] top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 md:w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] py-[2rem] px-0 shadow-lg shadow-[#444]"
          open={dialogState}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[80vw] md:w-[90vw]"
              onSubmit={handleSubmit}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer absolute top-[5%] left-[5%] hover:opacity-90"
                type="button"
                onClick={() => setDialogState(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  {t("Change Payment Details")}
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="amount_paid"
                >
                  {t("Amount Paid")} ({invoice ? invoice.amount_paid : ""} KWD)
                </label>
                <input
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step="0.1"
                  placeholder={`${t("Amount Paid")}`}
                  onChange={(e) => setAmountPaid(e.target.value)}
                />
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_status"
                >
                  {t("Payment Status")} (
                  {invoice ? t(`${invoice.payment_status}`) : ""})
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  onChange={(e) => setPaymentStatus(e.target.value)}
                >
                  <option value="-- Select payment status --">
                    -- Select payment status --
                  </option>
                  <option value="Unpaid">{t("Unpaid")}</option>
                  <option value="Partially Paid">{t("Partially Paid")}</option>
                  <option value="Paid">{t("Paid")}</option>
                  <option value="Other">{t("Other")}</option>
                </select>
              </div>

              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_type"
                >
                  {t("Payment Type")} ({invoice ? invoice.payment_type : ""})
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  name="payment_type"
                  onChange={(e) => setPaymentType(e.target.value)}
                >
                  <option value="-- Select payment type --">
                    -- Select payment type --
                  </option>
                  <option value="K-Net">K-Net</option>
                  <option value="Cash">Cash</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default ShowInvoice;
