import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import UserForm from "./UserForm";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const RegisterUser = () => {
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin"
    : "https://khamco-shop-backend.onrender.com/admin";
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState([]);
  const [language, setLanguage] = useState("en");
  const [userPhoto, setUserPhoto] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    let isMounted = true;

    document.title = "User Registration - Khamco Shop";

    return () => {
      isMounted = false;
    };
  }, [authKey, navigate, serverUrl]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
  
    if (!role) {
      setLoading(false);
      toast.info("Please select a role");
      return;
    }
  
    if (!firstName) {
      setLoading(false);
      toast.info("Name is required");
      return;
    }
  
    if (!language) {
      toast.info("User language is required");
      setLoading(false);
      return;
    }
  
    try {
      const formData = new FormData();
      formData.append("name", firstName);
      formData.append("surname", lastName);
      formData.append("email", email);
      formData.append("role", role);
      formData.append("language", language);
      formData.append("password", password);
      formData.append("password2", confirmPassword);
  
      // Append the photo file if it exists
      if (userPhoto) {
        formData.append("cover", userPhoto);
      }
  
      const response = await axios.post(`${serverUrl}/register`, formData, {
        headers: {
          Authorization: `Bearer ${authKey}`,
          "Content-Type": "multipart/form-data",
        },
      });
  
      toast.success(response.data.message);
      setAuthorizedUser(response.data.authorizedUser);
      navigate(`/admin/users/${response.data.user._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    } finally {
      setLoading(false);
    }
  };
  

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section>
          <h1 className="text-center text-[2rem] font-minimoBold">
            Register User
          </h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <UserForm
              page="register"
              loading={loading}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setUserPhoto={setUserPhoto}
              setEmail={setEmail}
              setPassword={setPassword}
              setConfirmPassword={setConfirmPassword}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default RegisterUser;
