import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { apiUrl, authKey } from "../../utilities";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

const AdminExpenditures = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const chartRef = useRef(null);
  const [salaries, setSalaries] = useState(0);
  const [costs, setCosts] = useState(0);
  const [rent, setRent] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Dashboard - Khamco Shop";

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/admin/dashboard`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        if (isMounted) {
          setSalaries(response.data.salaries);
          setCosts(response.data.costs);
          setRent(response.data.rent);
          setIsLoading(false);
        }
      } catch (error) {
        if (isMounted) {
          setIsLoading(true);
          if (error.response) {
            if (
              error.response.status === 403 ||
              error.response.status === 400
            ) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chartData = {
      labels: ["Employee Salaries", "Product Costs", "Rent"],
      datasets: [
        {
          label: "Revenue in KWD",
          data: [
            salaries || 0,
            costs || 0,
            rent || 0,
          ],
          backgroundColor: [
            "rgba(75, 192, 192, 0.6)",
            "rgba(54, 162, 235, 0.6)",
            "rgba(255, 206, 86, 0.6)",
            "rgba(153, 102, 255, 0.6)",
          ],
          borderColor: [
            "rgba(75, 192, 192, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(153, 102, 255, 1)",
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      scales: {
        y: {
          beginAtZero: true,
          ticks: {
            callback: function (value) {
              return new Intl.NumberFormat("kw-KW", {
                style: "currency",
                currency: "KWD",
                minimumFractionDigits: 3,
                maximumFractionDigits: 3,
              }).format(value);
            },
          },
        },
      },
    };

    const chart = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: options,
    });

    return () => {
      chart.destroy();
    };
  }, [salaries, costs, rent]);

  return (
    <main>
      <canvas
        id="revenueChart"
        width="400"
        height="150"
        ref={chartRef}
      ></canvas>
    </main>
  );
};

export default AdminExpenditures;
