import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import OrderForm from "./OrderForm";
import { Link } from "react-router-dom";
import axios from "axios";

const NewOrder = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [orders, setOrders] = useState([
    { choice: "Ham" },
    { choice: "Ham" },
    { choice: "Ham" },
  ]);
  const order = {
    customers: [
      {
        customer_name: "John",
      },
      {
        customer_name: "John",
      },
      {
        customer_name: "John",
      },
    ],
  };
  const [products, setProducts] = useState([
    {
      name: "Nike",
    },
    {
      name: "Nike",
    },
    {
      name: "Nike",
    },
  ]);
  const [services, setServices] = useState([
    {
      service: "Tailoring",
    },
    {
      service: "Tailoring",
    },
    {
      service: "Tailoring",
    },
  ]);
  const [customers, setCustomers] = useState([
    {
      name: "John",
    },
    {
      name: "John",
    },
    {
      name: "John",
    },
  ]);
  useEffect(() => {
    document.title = "New Order - Khamco Shop";
  }, []);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> Orders
            </Link>
          </div>
          <h1 className="title">New Order</h1>
          <p className="message">{message}</p>
          <div className="row">
            <h1 className="text-center text-2xl">Order Details</h1>
          </div>
          {orders &&
            orders.map((item) => (
              <form
                action="/orders/<%= order.id %>/names/<%= name.id %>?_method=DELETE"
                method="POST"
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <div className="form-row">
                  <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                    <label className="border-b-[0.1rem] border-[gray] p-b-[0.5rem] w-[85%]">
                      {item.choice}
                    </label>
                    <button
                      type="button"
                      className="delete-btn width-[10%]"
                      title="Remove this item"
                    >
                      <i className="fas fa-minus"></i>
                    </button>
                  </div>
                </div>
              </form>
            ))}
          <form
            action="/orders/<%= order.id %>/name?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="form-row">
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select name="order_name" id="order_name" className="w-[85%]">
                  <option label="-- Products --" selected disabled></option>
                  {products.map((product) => (
                    <option value={product.name} label={product.name}></option>
                  ))}
                  <option label="-- Services --" disabled></option>
                  {services.map((service) => (
                    <option
                      value={service.service}
                      label={service.service}
                    ></option>
                  ))}
                </select>
                <button
                  type="button"
                  className="edit-btn"
                  title="Add another item"
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
          </form>
          <div className="form-row">
            <h1 className="text-center text-2xl">Customer Details</h1>
          </div>
          {order.customers &&
            order.customers.map((customer) => (
              <form
                action="/orders/<%= order.id %>/customers/<%= customer.id %>?_method=DELETE"
                method="POST"
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <div className="form-row">
                  <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                    <label className="border-b-[0.1rem] border-b-gray-400 pb-[0.5rem] w-[85%]">
                      {customer.customer_name}
                    </label>
                    <button
                      type="button"
                      className="delete-btn w-[10%]"
                      title="Remove this item"
                    >
                      <i className="fas fa-minus"></i>
                    </button>
                  </div>
                </div>
              </form>
            ))}
          <form
            action="/orders/<%= order.id %>/customers?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="form-row">
              <select name="customer_name" id="customer_name">
                <option
                  label="-- Select customer --"
                  selected
                  disabled
                ></option>
                {customers.map((customer) => (
                  <option value={customer.name} label={customer.name}></option>
                ))}
              </select>
            </div>
            <div className="form-row">
              <label for="dependents">Dependent Name</label>
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select
                  name="dependent_name"
                  id="dependents"
                  className="w-[85%]"
                >
                  <option>Hello World</option>
                  <option>Hello World</option>
                  <option>Hello World</option>
                </select>
                <button
                  type="button"
                  className="edit-btn w-[10%]"
                  title="Add another customer"
                >
                  <i className="fas fa-plus"></i>
                </button>
              </div>
            </div>
          </form>
          <form
            action="/orders/<%= order.id%>?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <OrderForm />
          </form>
          <form
            action="/orders/<%= order.id%>?_method=DELETE"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <button type="button" className="delete-btn">
              Delete <i className="fas fa-times"></i>
            </button>
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewOrder;
