import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import { useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";

const NewInvoice = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  // API Data
  const [services, setServices] = useState([]);
  const [products, setProducts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [invoice, setInvoice] = useState({});
  // Product form fields
  const [productChoice, setProductChoice] = useState("");
  const [productQuantity, setProductQuantity] = useState(0);
  const [productPrice, setProductPrice] = useState(0);
  const [productAvailable, setProductAvailable] = useState(0);
  const productQuantityRef = useRef(null);
  const productChoiceRef = useRef(null);

  // Service form fields
  const [serviceChoice, setServiceChoice] = useState("");
  const [serviceQuantity, setServiceQuantity] = useState(0);
  const [servicePrice, setServicePrice] = useState(0);
  const serviceQuantityRef = useRef(null);
  const serviceChoiceRef = useRef(null);

  // Dependent form fields
  const [dependentName, setDependentName] = useState("");
  const [dependents, setDependents] = useState([]);
  const dependentNameRef = useRef(null);

  // Other form fields
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState(null);
  const [totalPrice, setTotalPrice] = useState(0);
  const [date, setDate] = useState(null);
  // Dynamic values
  const [loading, setLoading] = useState(false);
  const [loadingData, setLoadingData] = useState(true);

  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Invoice - Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();
  }, [apiUrl, authKey, id, userLanguage]);

  const clearForm = async () => {
    // Product form fields
    setProductChoice("");
    productChoiceRef.current.value = "-- Select a product --";
    setProductQuantity(0);
    setProductAvailable(0);
    setProductPrice(0);
    productQuantityRef.current.value = null;
    // Service form fields
    setServiceChoice("");
    serviceChoiceRef.current.value = "-- Select a service --";
    setServiceQuantity(0);
    setServicePrice(0);
    serviceQuantityRef.current.value = null;
    // Dependent form fields
    setDependentName("");
    dependentNameRef.current.value = "";
    dependentNameRef.current.label = "-- Select a Dependent --";
    setTotalPrice(0);
  };

  const handleProductChange = (e) => {
    const selectedProductName = e.target.value;

    if (selectedProductName === "-- Select a product --") {
      // Reset relevant states when the default option is selected
      setProductChoice("");
      setTotalPrice(0 + servicePrice * serviceQuantity);
      setProductAvailable(0);
      setProductPrice(0);
      return;
    }

    const selectedProduct = products.find(
      (product) => product.name === selectedProductName
    );

    if (selectedProduct) {
      setProductChoice(selectedProductName);
      setTotalPrice(
        0 +
          selectedProduct.price * productQuantity +
          servicePrice * serviceQuantity
      );
      setProductAvailable(selectedProduct.quantity);
      setProductPrice(selectedProduct.price);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="khamco-wrapper font-minimoBold"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> Invoices
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">
            New Invoice
          </h1>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewInvoice;
