import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import { apiUrl, authKey } from "../../utilities";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

const AdminDashboard = () => {
  const [availableFabric, setAvailableFabric] = useState(0);
  const [averageServiceTime, setAverageServiceTime] = useState({});
  const [customers, setCustomers] = useState([{}]);
  const [invoiceData, setInvoiceData] = useState([]);
  const [dailyRevenue, setDailyRevenue] = useState(null);
  const [salaries, setSalaries] = useState(0);
  const [costs, setCosts] = useState(0);
  const [rent, setRent] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const navigate = useNavigate();
  const { t } = useTranslation();
  const chartRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Dashboard - Khamco Shop";

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/admin/dashboard`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        if (isMounted) {
          setAvailableFabric(response.data.available_fabric);
          setAverageServiceTime(response.data.average_service_time);
          setCustomers(response.data.customers);
          setInvoiceData(response.data.invoice_data);
          setDailyRevenue(response.data.daily_revenue);
          setSalaries(response.data.salaries);
          setCosts(response.data.costs);
          setRent(response.data.rent);
          setDisplay(true);
          setIsLoading(false);
        }
      } catch (error) {
        if (isMounted) {
          setDisplay(false);
          setIsLoading(true);
          if (error.response) {
            if (
              error.response.status === 403 ||
              error.response.status === 400
            ) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chartData = {
      labels: ["Employee Salaries", "Product Costs", "Rent"],
      datasets: [
        {
          label: "Revenue in KWD",
          data: [salaries || 0, costs || 0, rent || 0],
          backgroundColor: [
            "rgba(75, 192, 192, 0.6)",
            "rgba(54, 162, 235, 0.6)",
            "rgba(255, 206, 86, 0.6)",
            "rgba(153, 102, 255, 0.6)",
          ],
          borderColor: [
            "rgba(75, 192, 192, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(153, 102, 255, 1)",
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      scales: {
        y: {
          beginAtZero: true,
          ticks: {
            callback: function (value) {
              return new Intl.NumberFormat("kw-KW", {
                style: "currency",
                currency: "KWD",
                minimumFractionDigits: 3,
                maximumFractionDigits: 3,
              }).format(value);
            },
          },
        },
      },
    };

    const chart = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: options,
    });

    return () => {
      chart.destroy();
    };
  }, [salaries, costs, rent]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          <h1 className="text-center text-[2rem] font-minimoBold">
            {t("Admin Dashboard")}
          </h1>
          {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          {/* {display && ( */}
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] my-[2rem] m-auto">
              <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  {t("Total Customers")}
                </h2>
                <canvas
                  id="revenueChart"
                  width="400"
                  height="150"
                  ref={chartRef}
                ></canvas>
              </div>
              {customers && (
                <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    {t("Total Customers")}
                  </h2>
                  <h2 className="text-xl font-semibold text-green-500">
                    {customers.length}
                  </h2>
                  <Link
                    to="/admin/customers-data"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  >
                    {t("Details")}
                  </Link>
                </div>
              )}
              <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  Today's Revenue
                </h2>
                <h2 className="text-xl font-semibold text-green-500">
                  {new Intl.NumberFormat("kw-KW", {
                    style: "currency",
                    currency: "KWD",
                    minimumFractionDigits: 3,
                    maximumFractionDigits: 3,
                  }).format(dailyRevenue[0]?.totalAmount) || 0}
                </h2>
                <Link
                  to="/admin/revenue"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                >
                  {t("Details")}
                </Link>
              </div>
              <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  {t("Total Fabric")}
                </h2>
                <h2 className="text-xl font-semibold text-green-500">
                  {availableFabric} meters
                </h2>
                <Link
                  to="/admin/fabrics"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                >
                  {t("Details")}
                </Link>
              </div>
              <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  {t("Average Service Time")}
                </h2>
                <h2 className="text-xl font-semibold text-green-500">{`${
                  averageServiceTime.days && averageServiceTime.days.toFixed(2)
                } day(s), ${
                  averageServiceTime.hours &&
                  averageServiceTime.hours.toFixed(2)
                } hours`}</h2>

                <Link
                  to="/admin/service-times"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                >
                  {t("Details")}
                </Link>
              </div>
              <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                <h2 className="text-khamco-secondary text-2xl font-bold">
                  {t("Invoice Data")}
                </h2>
                {invoiceData && (
                  <>
                    <h2 className="text-xl font-semibold text-green-500">{`${
                      invoiceData[0]?.total || 0
                    } created`}</h2>
                    <h2 className="text-xl font-semibold text-red-500">
                      {`${invoiceData[0]?.sum || 0} deleted`}
                    </h2>
                  </>
                )}
                <Link
                  to="/admin/invoice-data"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                >
                  {t("Details")}
                </Link>
              </div>
            </div>
          {/* )} */}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminDashboard;
