import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import MeasurementsForm from "./MeasurementsForm";
import Footer from "../../../components/Footer";
import { toast } from "react-toastify";
import axios from "axios";
import { apiUrl, authKey } from "../../../utilities";

const EditMeasurement = () => {
  const { id } = useParams();
  const { measurementId } = useParams();
  const [customer, setCustomer] = useState(null);
  const [measurement, setMeasurement] = useState(null);
  const navigate = useNavigate();
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [shirtType, setShirtType] = useState("");
  const [specialOrder, setSpecialOrder] = useState("");
  const [alterations, setAlterations] = useState("");
  const [notes, setNotes] = useState("");
  const [loading, setLoading] = useState(false);
  const [measurementValues, setMeasurementValues] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/customers/edit/${id}/measurements/${measurementId}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMeasurement(response.data.measurement);
          setDate(response.data.measurement.date_created);
          setClothingOptions(
            response.data.customer.measurements.find(
              (measurement) => measurement._id === measurementId
            ).clothing_options
          );
          // setMeasurementValues(
          //   response.data.customer.measurements.find(
          //     (measurement) => measurement._id === measurementId
          //   )
          // );
          console.log(
            response.data.customer.measurements.find(
              (measurement) => measurement._id === measurementId
            )
          );
          document.title = `Editing Measurement - ${measurementId}`;
        })
        .catch((error) => {
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          navigate(`/customers/${id}/measurements/${measurementId}`);
        });
    };

    return () => {
      fetchData();
    };
  }, [id, measurementId, navigate]);

  const handleSubmit = async (event) => {
    setLoading(true);
    event.preventDefault();
    try {
      console.log({
        date_created: date,
        clothing_options: clothingOptions,
        ...measurementValues,
        description: description,
        special_order: specialOrder,
        alterations: alterations,
        notes: notes,
      });
      // const response = await axios.put(
      //   `${apiUrl}/customers/${id}/measurements/${measurementId}`,
      //   {
      //     date_created: date,
      //     clothing_options: clothingOptions,
      //     ...measurementValues,
      //     description: description,
      //     special_order: specialOrder,
      //     alterations: alterations,
      //     notes: notes,
      //   },
      //   {
      //     headers: { Authorization: `Bearer ${authKey}` },
      //   }
      // );

      // toast.success(response.data.message);
      // setCustomer(response.data.customer);
      // navigate(
      //   `/customers/${response.data.customer._id}/measurements/${response.data.measurement._id}`
      // );
      setLoading(false);
    } catch (error) {
      setLoading(false);
      if (error.response) {
        const { status, data } = error.response;

        if (status === 404) {
          if (data.message === "Customer not found") {
            toast.error("Customer not found");
          } else if (data.message === "Dependent not found") {
            toast.error("Dependent not found");
          } else if (data.message === "Date is required") {
            toast.error("The date field is required");
          } else {
            toast.error("Resource not found");
          }
        } else if (status === 400) {
          toast.error("Invalid ObjectId format");
        } else if (status === 409) {
          toast.error(data.message);
        } else {
          toast.error("An error occurred while updating measurement details");
        }
      } else {
        toast.error("An error occurred while updating measurement details");
      }
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="khamco-wrapper">
          {customer && measurement && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer && customer._id}/measurements`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> Measurements
                </Link>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">{`Edit Measurements for ${new Date(
                measurement.date_created
              ).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
              })}`}</h1>
            </>
          )}
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">
                Enter New Date
              </label>
              <input
                className="p-2 border-2 border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="date"
                value={date ? new Date(date).toISOString().split("T")[0] : ""}
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </div>
            <MeasurementsForm
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              shirtType={shirtType}
              setShirtType={setShirtType}
              setDescription={setDescription}
              loading={loading}
              setSpecialOrder={setSpecialOrder}
              setAlterations={setAlterations}
              setNotes={setNotes}
              setValues={setMeasurementValues}
              values={measurementValues}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditMeasurement;
