import React, { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import OrderForm from "./OrderForm";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const NewOrder = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [order, setOrder] = useState([]);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [orderName, setOrderName] = useState("");
  const [dependentName, setDependentName] = useState("");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [customerDependents, setCustomerDependents] = useState([]);
  const [customerAddress, setCustomerAddress] = useState([]);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamcotailor.com`;
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Order - Khamco Shop";

    const fetchOrderDetails = async () => {
      await axios
        .get(`${apiUrl}/orders/create/new-order/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setOrder(response.data.order);
          setCustomers(response.data.customers);
          setProducts(response.data.products);
          setServices(response.data.services);
        })
        .catch((error) => {
          toast.error(error.message);
          console.error(error.message);
        });
    };

    fetchOrderDetails();
  }, [apiUrl, authKey, id]);

  const createOrderName = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (!orderName) {
      setLoading(false);
      toast.warn("Please select a value");
      return;
    }
    await axios
      .put(
        `${apiUrl}/orders/${id}/name`,
        {
          order_name: orderName,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setOrder(response.data.order);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        toast.error(error.message);
        console.error(error.message);
      });
  };

  const deleteOrderName = async (e, orderId, nameId) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/orders/${orderId}/names/${nameId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setOrder(response.data.order);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const addDependent = async (e) => {
    e.preventDefault();
    setLoading(true);
    if (!dependentName) {
      toast.warn("Please select a dependent from the list");
      setLoading(false);
      return;
    }

    await axios
      .put(
        `${apiUrl}/orders/${id}/dependents`,
        {
          dependent_name: dependentName,
          customer_name: customerName,
          customer_surname: customerSurname,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setOrder(response.data.order);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
        setLoading(false);
      })
      .catch(() => {
        setLoading(false);
        toast.error("Error updating the order.");
      });
  };

  const handleChange = async (e) => {
    const selectedOption = e.target.options[e.target.selectedIndex];
    setCustomerSurname(selectedOption.dataset.surname);
    setCustomerEmail(selectedOption.dataset.email);
    setCustomerPhone(selectedOption.dataset.phone);
    setCustomerAddress(selectedOption.dataset.address);
    setCustomerName(selectedOption.value);
    setCustomerDependents(
      (e.target.value = customers.find(
        (customer) => customer.name === e.target.value
      )).dependents
    );
  };

  const deleteEntireOrder = async (e) => {
    e.preventDefault();
    await axios
      .delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        toast.success(response.data.message);
        navigate("/orders");
      })
      .catch(() => {
        toast.error("Unable to delete order");
      });
  };

  const deleteDependent = async (e, orderId, customerId) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.delete(
        `${apiUrl}/orders/${orderId}/dependents/${customerId}`,
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setOrder(response.data.order);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios
      .put(apiUrl, {
        
      },{
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        navigate(`/orders/${response.data.order._id}`);
      })
      .catch((error) => {
        toast.error(error.message);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> Orders
            </Link>
          </div>
          <h1 className="title">New Order</h1>
          <form className="flex flex-col justify-center items-center w-[90vw]">
            <div className="form-row">
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select
                  className="w-[85%]"
                  onChange={(e) => setOrderName(e.target.value)}
                >
                  <option label="-- Products --"></option>
                  {products.map((product, index) => (
                    <option
                      key={index}
                      value={product.name}
                      label={product.name}
                    ></option>
                  ))}
                  <option label="-- Services --" disabled></option>
                  {services.map((service, index) => (
                    <option
                      key={index}
                      value={service.service}
                      label={service.service}
                    ></option>
                  ))}
                </select>
                <button
                  onClick={createOrderName}
                  className="edit-btn"
                  title="Add another item"
                  disabled={loading}
                >
                  {loading ? (
                    <i className="fas fa-spinner animate-spin"></i>
                  ) : (
                    <i className="fas fa-plus"></i>
                  )}
                </button>
              </div>
            </div>
          </form>
          <div className="form-row">
            <h1 className="text-center text-2xl">Order Details</h1>
          </div>
          <div className="border-t-2 border-b-2 border-black my-[2rem]">
            {order.name && order.name.length > 0 ? (
              order.name.map((item, index) => (
                <form
                  className="flex flex-col justify-center items-center w-[90vw]"
                  key={index}
                >
                  <div className="form-row">
                    <div className="flex gap-[1rem] w-full justify-start flex-wrap items-center">
                      <label className="border-b-[0.1rem] border-[gray] p-b-[0.5rem] w-[85%]">
                        {item.choice}
                      </label>
                      <button
                        type="button"
                        className="delete-btn width-[10%]"
                        title="Remove this item"
                        onClick={(e) => deleteOrderName(e, order._id, item._id)}
                        disabled={loading}
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <i className="fas fa-minus"></i>
                        )}
                      </button>
                    </div>
                  </div>
                </form>
              ))
            ) : (
              <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                Nothing here yet...
              </p>
            )}
          </div>
          <form className="flex flex-col justify-center items-center w-[90vw]">
            <div className="form-row">
              <label>Customer Name</label>
              <div className="flex w-full justify-between flex-wrap">
                <select onChange={handleChange}>
                  <option label="-- Select customer --"></option>
                  {customers &&
                    customers.map((customer, index) => (
                      <option
                        key={index}
                        value={customer.name}
                        label={customer.name}
                        data-surname={customer.surname}
                        data-email={customer.email}
                        data-phone={customer.phone}
                        data-address={customer.address}
                      ></option>
                    ))}
                </select>
                <input
                  type="text"
                  placeholder="Customer Name"
                  className="cursor-not-allowed"
                  defaultValue={customerName}
                  readOnly
                />
              </div>
            </div>
            <div className="form-row">
              <label>Dependent Name</label>
              <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                <select
                  className="w-[85%]"
                  onChange={(e) => setDependentName(e.target.value)}
                >
                  <option label="-- Select a dependent --" disabled></option>
                  {customerDependents &&
                    customerDependents.map((customer, index) => (
                      <option key={index}>{customer.name}</option>
                    ))}
                </select>
                <button
                  type="button"
                  className="edit-btn w-[10%]"
                  title="Add dependent to the list"
                  onClick={addDependent}
                >
                  {loading ? (
                    <i className="fas fa-spinner animate-spin"></i>
                  ) : (
                    <i className="fas fa-plus"></i>
                  )}
                </button>
              </div>
            </div>
          </form>
          <div className="form-row">
            <h1 className="text-center text-2xl">List of Dependents</h1>
          </div>
          <div className="border-t-2 border-b-2 border-black my-[2rem]">
            {order.customers && order.customers.length > 0 ? (
              order.customers.map((customer, index) => (
                <form className="flex flex-col justify-center items-center w-[90vw]">
                  <div className="form-row">
                    <div className="flex gap-[1rem] w-full justify-start flex-wrap">
                      <label
                        className="border-b-[0.1rem] border-b-gray-400 pb-[0.5rem] w-[85%]"
                        key={index}
                      >
                        {customer.customer_name}
                      </label>
                      <button
                        type="button"
                        className="delete-btn w-[10%]"
                        title="Remove this item"
                        onClick={(e) =>
                          deleteDependent(e, order._id, customer._id)
                        }
                      >
                        {loading ? (
                          <i className="fas fa-spinner animate-spin"></i>
                        ) : (
                          <i className="fas fa-minus"></i>
                        )}
                      </button>
                    </div>
                  </div>
                </form>
              ))
            ) : (
              <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                Nothing here yet...
              </p>
            )}
          </div>
          <form
            action="/orders/<%= order.id%>?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <OrderForm
              customerName={customerName}
              customerSurname={customerSurname}
              customerEmail={customerEmail}
              customerPhone={customerPhone}
              customerAddress={customerAddress}
              orderId={id}
            />
          </form>
          <form
            onSubmit={deleteEntireOrder}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <button
              type="submit"
              className="delete-btn w-1/4 py-[1rem] px-[0.5rem]"
            >
              Delete Order <i className="fas fa-times"></i>
            </button>
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewOrder;
