import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const AdminFabrics = () => {
  const [aggregateData, setAggregateData] = useState([]);
  const [highestProducts, setHighestProducts] = useState([]);
  const [lowestProducts, setLowestProducts] = useState([]);
  const [productQuantities, setProductQuantities] = useState({});
  const [products, setProducts] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Recent Activity");
  const [display, setDisplay] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/fabrics"
    : "https://khamco-shop-backend.onrender.com/admin/fabrics";
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = "Admin Fabric Data - Khamco Shop";
    const getData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setIsLoading(false);
          setDisplay(true);
          setAggregateData(response.data.aggregateData);
          setHighestProducts(response.data.highestProducts);
          setLowestProducts(response.data.lowestProducts);
          setProductQuantities(response.data.productQuantities);
          setProducts(response.data.products);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            navigate("/");
            toast.error(error.response?.data?.message || error.message);
          }
          return;
        });
    };

    getData();
  }, [apiUrl, authKey, navigate]);

  return (
    <>
      <Navbar />
      <main className="p-16">
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          {isLoading && (
            <>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
                <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              </div>
              <table className="text-center w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                <thead className="text-khamco-primary font-bold bg-[#ddd]">
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12 border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {display && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              {highestProducts &&
                highestProducts.map((product, index) => (
                  <div
                    className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]"
                    key={index}
                  >
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Highest Quantity
                    </h2>
                    <p>{product.name}</p>
                    <h2>
                      <Link
                        to={`/products/${product._id}`}
                        target="_blank"
                        className="text-xl font-semibold text-green-500"
                      >
                        {product.quantity} meters{" "}
                        <i className="fas fa-external-link"></i>
                      </Link>
                    </h2>
                  </div>
                ))}
              {lowestProducts &&
                lowestProducts.map((product, index) => (
                  <div
                    className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]"
                    key={index}
                  >
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Lowest Quantity
                    </h2>
                    <p>{product.name}</p>
                    <h2>
                      <Link
                        to={`/products/${product._id}`}
                        target="_blank"
                        className="text-red-500"
                      >
                        {product.quantity} meters{" "}
                        <i className="fas fa-external-link"></i>
                      </Link>
                    </h2>
                  </div>
                ))}
            </div>
          )}
          {display && (
            <div className="search-engine mt-[4rem]">
              <select
                className="search-input"
                onChange={(e) => setSelectedIndex(e.target.value)}
              >
                <option>-- Select Report --</option>
                <option>Recent Activity</option>
                <option>Fabric Consumed</option>
                <option>Available Fabric</option>
              </select>
            </div>
          )}
          {display && (
            <>
              {selectedIndex === "Recent Activity" && (
                <table id="recent-activity" className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">Recent Activity</caption>
                  <thead className="text-khamco-primary font-bold bg-[#ddd]">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td>Fabric</td>
                      <td>Date</td>
                      <td>Meters</td>
                    </tr>
                  </thead>
                  <tbody>
                    {aggregateData &&
                      aggregateData.map((data, index) => (
                        <tr key={index} className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                          <td>{data._id.choice}</td>
                          <td>
                            {new Date(
                              `${data._id.month}/${data._id.day}/${data._id.year}`
                            ).toLocaleDateString("en-US", {
                              month: "long",
                              day: "numeric",
                              year: "numeric",
                            })}
                          </td>
                          <td>{data.totalQuantity}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Fabric Consumed" && (
                <table id="fabric-consumed" className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">Fabric Consumed</caption>
                  <thead className="text-khamco-primary font-bold bg-[#ddd]">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td>Fabric</td>
                      <td>Total Consumption</td>
                    </tr>
                  </thead>
                  <tbody>
                    {productQuantities &&
                      Object.keys(productQuantities).map((product, index) => (
                        <tr key={index} className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                          <td>{product}</td>
                          <td>{productQuantities[product]} meters</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Available Fabric" && (
                <table id="available-fabric" className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">Available Fabric</caption>
                  <thead className="text-khamco-primary font-bold bg-[#ddd]">
                    <tr>
                      <td>Name</td>
                      <td>Meters</td>
                      <td>Category</td>
                      <td>Price</td>
                    </tr>
                  </thead>
                  <tbody>
                    {products &&
                      products.map((product, index) => (
                        <tr key={index}>
                          <td>{product.name}</td>
                          <td>{product.quantity.toFixed(2)}</td>
                          <td className="capitalize">{product.category}</td>
                          <td>KWD {product.price}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminFabrics;
