import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import CheckBox from "../../components/CheckBox";
import Selector from "../../components/Selector";
import { useTranslation } from "react-i18next";

const NewInvoice = () => {
  const [loading, setLoading] = useState(false);
  const [loadingData, setLoadingData] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/invoices"
    : "https://khamco-shop-backend.onrender.com/invoices";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [customers, setCustomers] = useState([]);
  const [allCustomers, setAllCustomers] = useState([]);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  // Form fields
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [dependentList, setDependentList] = useState([]);
  const [invoiceDetails, setInvoiceDetails] = useState([]);
  const [invoiceDate, setInvoiceDate] = useState("");
  const [paymentStatus, setPaymentStatus] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const [amountPaid, setAmountPaid] = useState("");
  const [totalPrice, setTotalPrice] = useState("");
  const [amountRemaining, setAmountRemaining] = useState("");

  const addQuantity = (name, quantity) => {
    setInvoiceDetails((prevArray) => {
      return prevArray.map((item) => {
        if (item.name === name) {
          const updatedItem = { ...item, quantity: Number(quantity) };
          return updatedItem;
        }
        return item;
      });
    });
  };

  useEffect(() => {
    document.title = `${t("New Invoice")} - Khamco Shop`;

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/create/new-invoice`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setAllCustomers(response.data.customers);
        setCustomers(response.data.customers);
        setProducts(response.data.products);
        setServices(response.data.services);
        setLoadingData(false);
      } catch (error) {
        setLoadingData(false);
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        } else {
          // Handle network errors or errors without a response object
          toast.error("An error occurred: " + error.message);
        }
      }
    };

    fetchData();

    const calculatedTotalPrice = invoiceDetails.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
    setTotalPrice(calculatedTotalPrice);
    setAmountRemaining(calculatedTotalPrice - Number(amountPaid));
  }, [apiUrl, authKey, navigate, invoiceDetails, amountPaid, t]);

  const handleChange = async (e) => {
    if (e.target.value === "-- Select customer --") {
      setDependentList([]);
      setCustomerName(null);
      return;
    }

    const currentCustomer = customers.find(
      (customer) => customer.name === e.target.value
    );

    setCustomerName(currentCustomer.name);
    setCustomerSurname(currentCustomer.surname);
    setCustomerPhone(currentCustomer.phone);
    setDependentList(currentCustomer.dependents);
  };

  const handleSubmit = async () => {
    setLoading(true);

    if (!customerName) {
      toast.warn("Please select a customer");
      setLoading(false);
      return;
    }

    if (!invoiceDate) {
      toast.warn("Please input the date");
      setLoading(false);
      return;
    }

    if (!paymentType) {
      toast.warn("Please select a payment type");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(
        apiUrl,
        {
          customer_name: customerName,
          customer_surname: customerSurname,
          customer_phone: customerPhone,
          dependents: dependentList,
          details: invoiceDetails,
          date: invoiceDate,
          payment_status: paymentStatus,
          payment_type: paymentType,
          amount_paid: amountPaid,
          total_price: totalPrice,
          amount_remaining: amountRemaining,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setLoading(false);
      toast.success(response.data.message);
      navigate(`/invoices/${response.data.invoice._id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
      setLoading(false);
      if (error.response) {
        if (error.response.status === 403) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const filterCustomers = (e) => {
    const inputPhone = e.target.value; // Get the current input value

    if (inputPhone === "") {
      setCustomers(allCustomers); // Reset to original list
      return;
    }

    const filteredCustomers = customers.filter(
      (customer) => customer.phone.includes(inputPhone) // Filter customers by the input phone number
    );

    setCustomers(filteredCustomers); // Update the customers state with the filtered list
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section
          className="khamco-wrapper font-minimoBold"
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> {t("Invoices")}
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">
            {t("New Invoice")}
          </h1>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <input
              type="tel"
              className={`p-2 border-2 rounded-md outline-none font-sans text-lg my-2 ${
                customers.length > 0 ? "border-[limegreen]" : "border-[crimson]"
              }`}
              placeholder="Search with customer phone number"
              onChange={(e) => filterCustomers(e)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("Customer Name")}
            </label>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading customer list...
              </p>
            )}
            {customers && (
              <select
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                onChange={handleChange}
              >
                <option
                  label="-- Select customer --"
                  value="-- Select customer --"
                ></option>
                {customers &&
                  customers.map((customer, index) => (
                    <option
                      key={index}
                      value={customer.name}
                      label={`${customer.name} ${customer.surname}`}
                    ></option>
                  ))}
              </select>
            )}
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            {dependentList.length > 0 && (
              <h1 className="text-center my-[1rem] text-xl font-bold">
                Dependents
              </h1>
            )}
            <div className="flex gap-[1rem] w-full justify-start flex-wrap">
              {dependentList.length > 0 &&
                dependentList.map((dependent, index) => (
                  <CheckBox
                    key={index}
                    label={dependent.name}
                    setOptions={setDependentList}
                  />
                ))}
              {!customerName && <p>Select a customer above</p>}
              {customerName && dependentList.length <= 0 && (
                <p>Customer has no dependents...</p>
              )}
            </div>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <h1 className="text-center my-[1rem] text-xl font-bold">
              {t("Product List")}
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading products...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-center md:justify-start flex-wrap">
              {products &&
                products.map((product, index) => (
                  <div className="flex flex-col items-center">
                    <Selector
                      key={index}
                      label={product.name}
                      price={product.price}
                      quantity={product.quantity}
                      type="product"
                      setOptions={setInvoiceDetails}
                    />
                    <p>
                      {t("Available")}:{" "}
                      <span
                        className={`text-lg ${
                          product.quantity <= 0 && "text-[red] font-bold"
                        }`}
                      >
                        {product.quantity.toFixed(0)}
                      </span>
                    </p>
                  </div>
                ))}
            </div>
            <h1 className="text-center my-[1rem] text-xl font-bold">
              {t("Service List")}
            </h1>
            {loadingData && (
              <p className="text-center my-[1rem] animate-pulse text-lg text-[orangered]">
                Loading services...
              </p>
            )}
            <div className="flex gap-[1rem] w-full justify-center flex-wrap">
              {services &&
                services.map((service, index) => (
                  <Selector
                    key={index}
                    label={service.service}
                    price={service.price}
                    quantity={1}
                    type="service"
                    setOptions={setInvoiceDetails}
                  />
                ))}
              {invoiceDetails.length > 0 && (
                <p className="text-center my-[1rem] font-bold text-lg text-[red]">
                  {t("Enter quantities below")}
                </p>
              )}
            </div>
            <div className="w-full flex flex-col mx-auto my-4 relative">
              {invoiceDetails.length > 0 && (
                <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem] overflow-auto">
                  <thead className="text-khamco-primary font-bold bg-[#ddd]">
                    <tr className="border-[0.2rem] border-khamco-secondary relative">
                      <th className="border-r-2 border-[blue]">{t("Item")}</th>
                      <th className="border-r-2 border-[blue]">{t("Price")}</th>
                      <th className="border-r-2 border-[blue]">
                        {t("Quantity")}
                      </th>
                      <th className="border border-gray-300 p-2">
                        {t("Total")}
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {invoiceDetails.map((item, index) => (
                      <tr
                        key={index}
                        className="border-[0.2rem] border-khamco-secondary border-t-0 relative"
                      >
                        <td className="p-[0.5rem] border-r-2 border-[blue]">
                          {item.name}
                        </td>
                        <td className="text-center p-[0.5rem] border-r-2 border-[blue]">
                          KWD{" "}
                          {new Intl.NumberFormat("en-US").format(item.price)}
                        </td>
                        <td className="border-r-2 border-[blue]">
                          <input
                            type="number"
                            value={item.quantity}
                            onChange={(e) =>
                              addQuantity(item.name, e.target.value)
                            }
                            className="text-center w-full border-2 border-black outline-none focus:border-khamco-primary"
                          />
                        </td>
                        <td className="text-center p-[0.5rem]">
                          {new Intl.NumberFormat("en-US").format(
                            item.price * item.quantity
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                  <tfoot>
                    <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                      <td
                        colSpan="3"
                        className="border border-gray-300 font-bold text-center p-[0.5rem]"
                      >
                        {t("Total Price")} (KWD)
                      </td>
                      <td className="border border-gray-300 font-bold text-center p-[0.5rem]">
                        {new Intl.NumberFormat("en-US").format(totalPrice)}
                      </td>
                    </tr>
                    <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                      <td
                        colSpan="3"
                        className="border border-gray-300 font-bold text-center p-[0.5rem]"
                      >
                        {t("Amount Remaining")} (KWD)
                      </td>
                      <td className="border border-gray-300 font-bold text-center p-[0.5rem]">
                        {new Intl.NumberFormat("en-US").format(amountRemaining)}
                      </td>
                    </tr>
                  </tfoot>
                </table>
              )}
            </div>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("Amount Paid")}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              type="number"
              step="0.1"
              placeholder="Amount Paid"
              onChange={(e) => setAmountPaid(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("Date")}
            </label>
            <input
              type="date"
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              onChange={(e) => setInvoiceDate(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label
              className="text-lg my-2 select-none self-start font-medium"
              htmlFor="payment_status"
            >
              {t("Payment Status")}
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              onChange={(e) => setPaymentStatus(e.target.value)}
            >
              <option value="Unpaid" selected>
                {t("Unpaid")}
              </option>
              <option value="Partially Paid">{t("Partially Paid")}</option>
              <option value="Paid">{t("Paid")}</option>
              <option value="Other">{t("Other")}</option>
            </select>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label
              className="text-lg my-2 select-none self-start font-medium"
              htmlFor="payment_type"
            >
              {t("Payment Type")}
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
              name="payment_type"
              onChange={(e) => setPaymentType(e.target.value)}
            >
              <option value="-- Select payment type --">
                -- Select payment type --
              </option>
              <option value="K-Net">K-Net</option>
              <option value="Cash">Cash</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <button
              className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
              onClick={handleSubmit}
            >
              {loading ? (
                <i className="fas fa-spinner animate-spin"></i>
              ) : (
                `${t("Submit")}`
              )}
            </button>
          </div>
        </section>
      </main>
      {invoiceDetails.length > 0 && (
        <div className="bg-khamco-primary py-1 px-[0.5rem] min-w-10 fixed top-[50%] right-[10%] rounded-full text-[1.5rem] text-white text-center font-bold border-2 border-khamco-secondary">
          {invoiceDetails.length || 0}
        </div>
      )}
      <Footer />
    </>
  );
};

export default NewInvoice;
