import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { apiUrl, authKey } from "../../utilities";
import { useTranslation } from "react-i18next";

const AdminTailors = () => {
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const location = useLocation();
  const { t } = useTranslation();
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalUsers, setTotalUsers] = useState(0);

  useEffect(() => {
    document.title = "Admin Tailor Data - Khamco Shop";

    const fetchUsers = async (queryParams = "") => {
      await axios
        .get(`${apiUrl}/admin/tailors/${queryParams}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setUsers(response.data.users);
          setTotalPages(response.data.totalPages);
          setTotalUsers(response.data.totalUsers);
          setDisplay(true);
          setIsLoading(false);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          return;
        });
    };

    const searchParams = new URLSearchParams(location.search);
    searchParams.set("page", currentPage); // Add page param to the query
    fetchUsers(`?${searchParams.toString()}`);
  }, [navigate, location.search, currentPage]);

  const deleteUser = async (userId) => {
    setLoading(true);
    try {
      const response = await axios.delete(`${apiUrl}/admin/tailors/${userId}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      setUsers(response.data.users);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form);
    searchParams.set("page", currentPage); // Ensure current page is included in search
    navigate(`?${searchParams.toString()}`);
  };

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section
          id="users-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                placeholder="Search Tailor Name"
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <select
                name="role"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              >
                <option value="">-- Select User Role --</option>
                <option value="Measurer">Measurer</option>
                <option value="Cutter">Cutter</option>
                <option value="Tailor">Tailor</option>
                <option value="Driver">Driver</option>
              </select>
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold my-[2rem]">
            {t("All Tailors")} ({users.length || 0})
          </h1>
          <div className="utilities">
            <Link
              className="text-[blue] no-underline hover:underline cursor-pointer"
              to="/users/register-user"
            >
              {t("Add New Tailor")} <i className="fas fa-user-plus"></i>
            </Link>
          </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] animate-pulse"
                ></div>
              ))}
          {display && (
            <>
              {users && users.length > 0 ? (
                users.map((user, index) => (
                  <div
                    className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                    key={index}
                  >
                    <p>
                      <strong>{t("Name")}</strong>: {user.name}
                    </p>
                    <p className="capitalize">
                      <strong>{t("Roles")}</strong>: ({user.role.length})
                    </p>
                    <p>
                      <strong>{t("Email")}</strong>:{" "}
                      <a
                        className="text-[blue] no-underline hover:underline cursor-pointer"
                        href={`mailto:${user.email}`}
                      >
                        {user.email}
                      </a>
                    </p>
                    <Link
                      className="text-[blue] no-underline hover:underline cursor-pointer w-full md:w-[5rem] text-center"
                      to={`/admin/users/${user._id}`}
                    >
                      {t("View")}
                    </Link>
                    <button
                      className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer w-full md:w-[5rem]"
                      disabled={loading}
                      onClick={() => deleteUser(user._id)}
                    >
                      {loading ? (
                        <i className="fas fa-spinner animate-spin"></i>
                      ) : (
                        <i className="fas fa-trash"></i>
                      )}
                    </button>
                  </div>
                ))
              ) : (
                <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">
                  No users in the database
                </p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailors;
