import React, { useEffect, useState } from "react";
import ServiceForm from "./ServiceForm";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link } from "react-router-dom";
import toast from "react-toastify";
import axios from "axios";

const NewService = () => {
  const [message, setMessage] = useState("");

  useEffect(() => {
    document.title = "New Service - Khamco Shop";

    toast.info("Welcome Mate!")
  }, []);

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="p-[4rem] grid justify-center items-center">
          <div className="row">
            <Link to="/services">
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          <h1 className="title">New Service</h1>
          <p className="message">{message}</p>
          <form
            action="/services"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <ServiceForm />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewService;
