import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";
import MeasurementList from "./MeasurementList";
import Footer from "../../../components/Footer";
import { useTranslation } from "react-i18next";

const MeasurementDetails = () => {
  const { id } = useParams();
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const { measurementId } = useParams();
  const [customer, setCustomer] = useState(null);
  const [measurement, setMeasurement] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [notes, setNotes] = useState(null);
  const [order, setOrder] = useState({});
  const [loading, setLoading] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();
  const detailsRef = useRef();
  const { t } = useTranslation();

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      try {
        const response = await axios.get(
          `${apiUrl}/customers/${id}/measurements/${measurementId}`,
          {
            headers: { Authorization: `Bearer ${authKey}` },
          }
        );

        if (isMounted) {
          setIsLoading(false);
          setCustomer(response.data.customer);
          setMeasurement(response.data.measurement);
          setOrder(response.data.order);
          document.title = `Measurement - ${response.data.measurement._id}`;
        }
      } catch (error) {
        if (isMounted) {
          setIsLoading(false);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          setCustomer(null);
          setMeasurement(null);
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, navigate]);

  const handleDelete = async (e) => {
    e.preventDefault();
    await axios
      .delete(`${apiUrl}/customers/${id}/measurements/${measurementId}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        toast.success(response.data.message);
        navigate(`/customers/${response.data.customer._id}/measurements`);
      })
      .catch((error) => {
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        } else {
          // Handle network errors or errors without a response object
          toast.error("An error occurred: " + error.message);
        }
      });
  };

  const submitNotes = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!notes) {
      toast.warn("Please write your notes");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.put(
        `${apiUrl}/customers/${id}/measurements/${measurementId}`,
        {
          notes: notes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setCustomer(response.data.customer);
      setMeasurement(response.data.measurement);
      setLoading(false);
      setOpenDialog(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = detailsRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
        <title>Invoice</title>
        <style>
        body {
          padding: 2rem;
          font-family: Arial, Helvetica, sans-serif;
        }
        .details-ref {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }
        .details-ref-span {
          display: flex;
          justify-content: start;
          gap: 2rem;
          text-wrap: balance;
          flex-wrap: wrap;
          margin: 1rem 0;
        }
        .change-button {
          display: none;
        }
        .converted-measurements {
          width: 100%;
          display: grid;
          grid-template-columns: repeat(3, minmax(0, 1fr));
          gap: 3rem;
          justify-content: center;
          min-height: 100vh;
        } 
        .measurement-items {
          margin: 2rem auto;
          display: flex;
          flex-direction: column;
          justify-content: start;
          text-wrap: balance;
          flex-wrap: wrap;
          border: 2px solid black;
          padding: 1rem;
          background-color: #ddd;
          align-items: center;
          gap: 0.5rem;
          border-radius: 1.5rem;
          font-size: 1.125rem;
        }
        .measurement-items img {
            width: 12rem;
            border: 2px solid #ed1965;
        }
        .clothing-options {
          display: flex;
          gap: 1rem;
        }
        .clothing-options strong {
          width: 25%;
        }
        .converted-flex {
          display: flex;
          flex-direction: column;
          justify-content: start;
          text-wrap: balance;
          flex-wrap: wrap;
          padding: 0.5rem 0;
          border-top: 2px solid black;
          gap: 0.5rem;
          font-size: 1.125rem;
        }
        .converted-flex img {
          display: none;
        }
        .notes-container {
            display: flex;
            flex-direction: column;
            justify-content: start;
            gap: 2rem;
        }
        .notes-container-strong {
            width: 100%;
            text-decoration-line: underline;
            font-weight: bold;
        }
        .notes-container-p {
            border-top: 2px solid black;
            background-color: #444;
            color: white;
            display: flex;
            flex-direction: column;
            gap: 1rem;
            padding: 1rem;
        }
        .notes-container-h4 {
            color: gold;
            text-decoration-line: underline;
        }

        .notes-container-last-span {
            justify-self: end;
        }     
        </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="customer-details">
          {isLoading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(3).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-3/4 bg-[#666] animate-pulse m-auto h-[100vh] my"></div>
                </div>
              </div>
            </>
          )}
          {customer && measurement && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}/measurements`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> {t("Measurements")}
                </Link>
              </div>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-evenly gap-8 items-center mx-auto my-8 w-full flex-wrap">
                  <Link
                    to={`/customers/edit/${customer._id}/measurements/${measurement._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    {t("EditButton")}
                  </Link>
                  <form onSubmit={handleDelete}>
                    <button
                      type="submit"
                      className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                    >
                      {t("Delete")}
                    </button>
                  </form>
                  <button
                    className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                    onClick={handlePrint}
                  >
                    {t("Print")} <i className="fas fa-print"></i>
                  </button>
                </div>
                <div
                  className="flex flex-col gap-[1rem] details-ref"
                  ref={detailsRef}
                >
                  <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>{t("Measurements Taken By")}: </strong>
                    {measurement.taken_by || "--"}
                  </span>
                  <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>{t("Created By")}: </strong>
                    {measurement.created_by || "--"}
                  </span>
                  {measurement.edited_by && (
                    <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                      <strong>{t("Edited By")}: </strong>
                      {measurement.edited_by}
                    </span>
                  )}
                  {measurement.date_edited && (
                    <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                      <strong>Edited on:</strong>
                      {new Date(measurement.date_edited).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                    </span>
                  )}
                  <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>{t("Order No.")}: </strong>
                    {order?.uniqueId || "--"}
                  </span>
                  <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>{t("Date Measurements Taken")}:</strong>
                    {new Date(measurement.date_created).toLocaleDateString(
                      "en-us",
                      {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      }
                    )}
                  </span>
                  <span className="details-ref-span inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong>{t("Customer ID No")}: </strong>
                    {customer.uniqueId || "--"}
                  </span>
                  <MeasurementList measurement={measurement} />
                  <h2 className="notes-container-strong w-full underline font-bold my-[1rem]">
                    {t("Notes")}:{" "}
                  </h2>
                  <span className="notes-container flex flex-col w-full m-auto md:w-2/4 justify-start gap-[2rem]">
                    {measurement.notes &&
                      measurement.notes.map((item, index) => (
                        <p
                          key={index}
                          className="notes-container-p shadow-md shadow-[#333] p-[1rem] rounded-md bg-[#444] text-[white] flex flex-col gap-[1rem]"
                        >
                          <span className="notes-container-h4 text-[gold] underline">
                            {item.user || "----"}
                          </span>
                          <span>{item.text}</span>
                          <span className="notes-container-last-span self-end">
                            {new Date(item.timestamp).toLocaleDateString(
                              "en-us",
                              {
                                day: "numeric",
                                month: "long",
                                year: "numeric",
                              }
                            )}
                            {", "}
                            {new Date(item.timestamp).toLocaleTimeString(
                              "en-us",
                              {
                                hour: "numeric",
                                minute: "numeric",
                              }
                            )}
                          </span>
                        </p>
                      ))}
                  </span>
                </div>
                <button
                  className="w-full md:w-2/4 m-auto py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  onClick={() => setOpenDialog(true)}
                >
                  {t("Add Note")} <i className="fas fa-plus"></i>
                </button>
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
      <dialog
        className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-[90%] md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0"
        open={openDialog}
      >
        <div className="p-[4rem] grid justify-center items-center w-full">
          <form
            className="flex flex-col justify-center items-center w-[80vw]"
            onSubmit={submitNotes}
          >
            <button
              className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
              type="button"
              onClick={() => setOpenDialog(false)}
            >
              <i className="fas fa-times"></i>
            </button>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <h2 className="text-center text-[2rem] font-minimoBold">
                {t("Add Note")}
              </h2>
            </div>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">
                {t("Notes")}
              </label>
              <textarea
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                placeholder="Enter your note here"
                onChange={(e) => setNotes(e.target.value)}
              ></textarea>
            </div>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <button
                type="submit"
                className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
              >
                {loading ? (
                  <i className="fas fa-spinner animate-spin"></i>
                ) : (
                  t("Submit")
                )}
              </button>
            </div>
          </form>
        </div>
      </dialog>
    </>
  );
};

export default MeasurementDetails;
