import MeasurementsForm from "./measurements/MeasurementsForm";
import { selectArea } from "../../utilities";
import { useTranslation } from "react-i18next";
import { useEffect, useState } from "react";

const CustomerForm = ({
  membershipStatus,
  setCustomerName,
  setCustomerSurname,
  setCustomerEmail,
  setCustomerPhone,
  setHouseUnit,
  setGovernorate,
  setArea,
  setCountry,
  militaryStatus,
  setMilitaryStatus,
  setMilitaryNumber,
  setMilitaryRank,
  setMilitaryDivision,
  setMembershipStatus,
  setDate,
  areas,
  setAreas,
  clothingOptions,
  setClothingOptions,
  setDishdashaLength,
  setDishdashaFrontHeight,
  setDishdashaBackHeight,
  shirtType,
  setShirtType,
  setGarmentLength,
  setPantsLength,
  setShortsLength,
  setDressLength,
  setCollar,
  setShoulders,
  setSleeve,
  setShirtSleeve,
  setDressSleeve,
  setDescription,
  setSleepwearSleeve,
  setChest,
  setBelly,
  setWaist,
  setHips,
  setArmHole,
  setBicepTricep,
  loading,
  setElbow,
  setWrist,
  setInseam,
  setThigh,
  setCalf,
  setLegFoot,
  setBackWidth,
  setBackHeight,
  setFrontChestWidth,
  setSpecialOrder,
  setAlterations,
  setNotes,
  customer,
  isCreateCustomer,
  customerName,
  customerSurname,
  customerEmail,
  customerPhone,
  houseUnit,
  governorate
}) => {
  const { t } = useTranslation();

  // Make sure the governorate is passed down properly
  const [selectedGovernorate, setSelectedGovernorate] = useState(governorate || "");

  useEffect(() => {
    if (governorate) {
      setSelectedGovernorate(governorate); // When governorate prop updates, update local state
    }
  }, [governorate]);

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Customer Name")}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder={`${t("Customer Name")}`}
          value={customerName}
          onChange={(e) => setCustomerName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Customer Surname")}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder={`${t("Customer Surname")}`}
          value={customerSurname}
          onChange={(e) => setCustomerSurname(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Email")}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="email"
          placeholder={"(Optional)"}
          value={customerEmail}
          onChange={(e) => setCustomerEmail(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Phone Number")}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder="Customer phone number e.g. +965000000000"
          value={customerPhone}
          onChange={(e) => setCustomerPhone(e.target.value)}
        />
      </div>
      {setHouseUnit && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            {t("House Unit")}
          </label>
          <input
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            type="text"
            placeholder="Enter street address"
            value={houseUnit}
            onChange={(e) => setHouseUnit(e.target.value)}
          />
        </div>
      )}
      {setGovernorate && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            {t("Governorate")}
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => {
          const selectedValue = e.target.value;
          setSelectedGovernorate(selectedValue);
          setGovernorate(selectedValue); // Propagate value to parent component
          setAreas([]); // Reset areas or update it based on the selection
        }}
        value={selectedGovernorate}
          >
            <option value="">-- Select the governorate --</option>
            <option value="Al Aasima">Al Aasima</option>
            <option value="Hawalli">Hawalli</option>
            <option value="Mubarak Al-Kabeer">Mubarak Al-Kabeer</option>
            <option value="Ahmadi">Ahmadi</option>
            <option value="Farwaniya">Farwaniya</option>
            <option value="Jahra">Jahra</option>
          </select>
        </div>
      )}
      {setArea && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            {t("Area")}
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => setArea(e.target.value)}
          >
            <option value="">-- Select the area --</option>
            {areas &&
              areas.map((area, index) => <option key={index}>{area}</option>)}
          </select>
        </div>
      )}
      {setCountry && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            {t("Country")}
          </label>
          <select
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            onChange={(e) => {
              setCountry(e.target.value);
            }}
            value={"Kuwait"}
          >
            <option disabled>-- Select country --</option>
            <option value="Kuwait">Kuwait</option>
          </select>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Military Status")}
        </label>
        <div className="flex gap-4 w-full justify-center flex-wrap">
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="civilian"
            >
              Civilian{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              id="civilian"
              type="checkbox"
              value="Civilian"
              checked={militaryStatus === "Civilian"}
              onChange={() => {
                setMilitaryStatus("Civilian");
              }}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="military"
            >
              Military{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              type="checkbox"
              value="Military"
              id="military"
              checked={militaryStatus === "Military"}
              onChange={() => {
                setMilitaryStatus("Military");
              }}
            />
          </span>
        </div>
      </div>
      {militaryStatus === "Military" && (
        <div className="w-full bg-khamco-ribbon text-white transition-all fadeIn">
          <h4 className="text-center text-[2rem] font-minimoBold">
            {t("Military Details")}
          </h4>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("ID Number")}{" "}
              {customer ? `(${customer.military_idnumber})` : ""}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              type="text"
              placeholder="Military ID Number"
              onChange={(e) => setMilitaryNumber(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("Rank")} {customer ? `(${customer.military_rank})` : ""}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              type="text"
              placeholder="Military Position"
              onChange={(e) => setMilitaryRank(e.target.value)}
            />
          </div>
          <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
            <label className="text-lg my-2 select-none self-start font-medium">
              {t("Department")}{" "}
              {customer ? `(${customer.military_department})` : ""}
            </label>
            <select
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] text-black"
              onChange={(e) => setMilitaryDivision(e.target.value)}
            >
              <option value="">-- Select military division --</option>
              <option value="Military (Other)">Military (Other)</option>
              <option value="Police">Police</option>
              <option value="OIL Dept.">OIL Dept.</option>
              <option value="Fire Fighting">Fire Fighting</option>
              <option value="Ministry (Other)">Ministry (Other)</option>
              <option value="Special Operations">Special Operations</option>
            </select>
          </div>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Membership Status")}
        </label>
        <div className="flex gap-4 w-full justify-center flex-wrap">
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="basic"
            >
              Basic{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              type="checkbox"
              value="Basic"
              id="basic"
              checked={membershipStatus === "Basic"}
              onChange={() => {
                setMembershipStatus("Basic");
              }}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="VIP"
            >
              VIP <i className="fas fa-star text-[#d2b929] animate-pulse"></i>{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
              type="checkbox"
              value="VIP"
              id="VIP"
              checked={membershipStatus === "VIP"}
              onChange={(e) => {
                setMembershipStatus("VIP");
              }}
            />
          </span>
        </div>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          {t("Date")}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="date"
          placeholder={
            customer
              ? new Date(customer.date_created).toISOString().split("T")[0]
              : null
          }
          onChange={(e) => {
            setDate(e.target.value);
          }}
        />
      </div>
      {isCreateCustomer && (
        <MeasurementsForm
          clothingOptions={clothingOptions}
          setClothingOptions={setClothingOptions}
          setDishdashaLength={setDishdashaLength}
          setDishdashaFrontHeight={setDishdashaFrontHeight}
          setDishdashaBackHeight={setDishdashaBackHeight}
          shirtType={shirtType}
          setShirtType={setShirtType}
          setGarmentLength={setGarmentLength}
          setPantsLength={setPantsLength}
          setShortsLength={setShortsLength}
          setDressLength={setDressLength}
          setCollar={setCollar}
          setShoulders={setShoulders}
          setSleeve={setSleeve}
          setShirtSleeve={setShirtSleeve}
          setDressSleeve={setDressSleeve}
          setDescription={setDescription}
          setSleepwearSleeve={setSleepwearSleeve}
          setChest={setChest}
          setBelly={setBelly}
          setWaist={setWaist}
          setHips={setHips}
          setArmHole={setArmHole}
          setBicepTricep={setBicepTricep}
          loading={loading}
          areas={areas}
          setElbow={setElbow}
          setWrist={setWrist}
          setInseam={setInseam}
          setThigh={setThigh}
          setCalf={setCalf}
          setLegFoot={setLegFoot}
          setBackWidth={setBackWidth}
          setBackHeight={setBackHeight}
          setFrontChestWidth={setFrontChestWidth}
          setSpecialOrder={setSpecialOrder}
          setAlterations={setAlterations}
          setNotes={setNotes}
        />
      )}
    </>
  );
};

export default CustomerForm;
