import React from "react";

const MeasurementList = ({ measurement }) => {
  return (
    <>
      <span className="gap-[0.1rem] flex">
        <strong className="w-2/4">Clothing Options: </strong>
        <div>
          {measurement.clothing_options &&
          measurement.clothing_options.length > 0 ? (
            measurement.clothing_options.map((item, index) => (
              <span key={index}> {item.clothing_item},</span>
            ))
          ) : (
            <p className="text-[red]">No clothing items selected.</p>
          )}
        </div>
      </span>
      {measurement.shirt_type && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shirt Type</strong>
          {measurement.shirt_type}
        </span>
      )}
      {measurement.garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Garment Length</strong>
          {`${measurement.garment_length} inches.`}
        </span>
      )}
      {measurement.pants_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Pants Length</strong>
          {`${measurement.pants_garment_length} inches.`}
        </span>
      )}
      {measurement.shorts_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shorts Length</strong>
          {`${measurement.shorts_garment_length} inches.`}
        </span>
      )}
      {measurement.dress_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dress Length</strong>
          {`${measurement.dress_garment_length} inches.`}
        </span>
      )}
      {measurement.collar > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Collar</strong>
          {`${measurement.collar} inches.`}
        </span>
      )}
      {measurement.shoulders > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shoulders</strong>
          {`${measurement.shoulders} inches.`}
        </span>
      )}
      {measurement.sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Sleeve</strong>
          {`${measurement.sleeve} inches.`}
        </span>
      )}
      {measurement.shirt_sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shirt Sleeve</strong>
          {`${measurement.shirt_sleeve} inches.`}
        </span>
      )}
      {measurement.dress_sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dress Sleeve</strong>
          {`${measurement.dress_sleeve} inches.`}
        </span>
      )}
      {measurement.sleepwear_sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Sleepwear Sleeve</strong>
          {`${measurement.sleepwear_sleeve} inches.`}
        </span>
      )}
      {measurement.chest > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Chest</strong>
          {`${measurement.chest} inches.`}
        </span>
      )}
      {measurement.belly > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Belly</strong>
          {`${measurement.belly} inches.`}
        </span>
      )}
      {measurement.waist > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Waist</strong>
          {`${measurement.waist} inches.`}
        </span>
      )}
      {measurement.hips > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Hips</strong>
          {`${measurement.hips} inches.`}
        </span>
      )}
      {measurement.arm_hole > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Arm Hole</strong>
          {`${measurement.arm_hole} inches.`}
        </span>
      )}
      {measurement.elbow > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Elbow</strong>
          {`${measurement.elbow} inches.`}
        </span>
      )}
      {measurement.wrist > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Wrist</strong>
          {`${measurement.wrist} inches.`}
        </span>
      )}
      {measurement.inseam > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Inseam</strong>
          {`${measurement.inseam} inches.`}
        </span>
      )}
      {measurement.thighs > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Thighs</strong>
          {`${measurement.thighs} inches.`}
        </span>
      )}
      {measurement.calf > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Calf</strong>
          {`${measurement.calf} inches.`}
        </span>
      )}
      {measurement.leg_foot > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Leg-Foot</strong>
          {`${measurement.leg_foot} inches.`}
        </span>
      )}
      {measurement.back_width > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Back Width</strong>
          {`${measurement.back_width} inches.`}
        </span>
      )}
      {measurement.back_height > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Back Width</strong>
          {`${measurement.back_height} inches.`}
        </span>
      )}
      {measurement.special_order && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong className="text-khamco-primary animate-pulse">
            Special Order:
          </strong>
          <p className="border-khamco-primary border-2 p-[1rem] border-dotted">
            {measurement.special_order}
          </p>
        </span>
      )}
      {measurement.notes && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Description/Notes:</strong>
          <p>{measurement.notes}</p>
        </span>
      )}
      {measurement.description && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Description/Notes:</strong>
          <p>{measurement.description}</p>
        </span>
      )}
    </>
  );
};

export default MeasurementList;
