import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { applyAnimation } from "../../utilities";

const MyOrders = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/orders/my-orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setOrders(response.data.orders);
            setIsLoading(false);
            setUser(response.data.user);
            document.title = `Order Management - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, navigate, serverUrl]);

  const determineDuty = (order) => {
    const activeUser = `${user.name} ${user.surname}`;
    let roles = [];
    if (activeUser === order.measurer) {
      roles.push("Measuring");
    }

    if (activeUser === order.sewer) {
      roles.push("Sewing");
    }

    if (activeUser === order.cutter) {
      roles.push("Cutting");
    }
    
    if (activeUser === order.driver) {
      roles.push("Driver");
    }

    return roles.join(", ");
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="py-0 px-[2rem] justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link to="/orders" className="text-[blue] no-underline hover:underline cursor-pointer">
              <i className="fas fa-arrow-left"></i> Back to Orders
            </Link>
          </div>
          {isLoading && (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto text-center">
              <thead className="text-khamco-primary font-bold bg-[#ddd]">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2">Loading...</td>
                </tr>
              </thead>
              <tbody>
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td className="text-center p-2">k</td>
                </tr>
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td className="text-center p-2">k</td>
                </tr>
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td className="text-center p-2">k</td>
                </tr>
              </tbody>
            </table>
          )}
          {orders && orders.length > 0 ? (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">My Orders</caption>
              <thead className="text-khamco-primary font-bold bg-[#ddd]">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2">Date</td>
                  <td className="text-center p-2">Status</td>
                  <td className="text-center p-2">Duty</td>
                  <td className="text-center p-2"></td>
                </tr>
              </thead>
              <tbody>
                {orders.map((order, index) => (
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative" key={index}>
                    <td className="text-center p-2">
                      {new Date(order.order_date).toLocaleDateString("en-us", {
                        month: "long",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </td>
                    <td className="text-center p-2">
                      <span
                        className={`${applyAnimation(
                          order.order_status
                        )} p-[0.2rem] rounded-[0.7rem]`}
                      >
                        {order.order_status}
                      </span>
                    </td>
                    <td className="text-center p-2 flex flex-col">{determineDuty(order)}</td>
                    <td className="text-center p-2">
                      <Link to={`/orders/${order._id}`} target="_blank" className="text-[blue] no-underline hover:underline cursor-pointer">
                        Details <i className="fas fa-external-link"></i>
                      </Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center my-[2rem] animate-pulse text-lg text-[orangered]">You have no orders...</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default MyOrders;
