import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { applyAnimation } from "../../utilities";
import { formatDistanceToNow } from "date-fns";

const OrderDetails = () => {
  const { id } = useParams();
  const [openDialog, setOpenDialog] = useState(false);
  const [openComment, setOpenComment] = useState(false);
  const [order, setOrder] = useState({});
  const [invoice, setInvoice] = useState({});
  const [customer, setCustomer] = useState({});
  const [measurerUsers, setMeasurerUsers] = useState([]);
  const [cutterUsers, setCutterUsers] = useState([]);
  const [tailorUsers, setTailorUsers] = useState([]);
  const [driverUsers, setDriverUsers] = useState([]);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const [userRole, setUserRole] = useState(null);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [tailor, setTailor] = useState("");
  const [driver, setDriver] = useState("");
  const [orderStatus, setOrderStatus] = useState("");
  const [productItems, setProductItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [comments, setComments] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const orderStatusRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Details - Khamco Shop";

    const fetchOrder = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setLoading(false);
          setOrder(response.data.order);
          setInvoice(response.data.invoice);
          setCustomer(response.data.customer);
          setMeasurerUsers(
            response.data.users.filter((user) => user.role === "Measurer")
          );
          setCutterUsers(
            response.data.users.filter((user) => user.role === "Cutter")
          );
          setTailorUsers(
            response.data.users.filter((user) => user.role === "Tailor")
          );
          setDriverUsers(
            response.data.users.filter((user) => user.role === "Driver")
          );
          setAuthorizedUser(
            `${response.data.authorizedUser.name} ${response.data.authorizedUser.surname}`
          );
          setUserRole(response.data.authorizedUser.role);
        }
      } catch (error) {
        if (isMounted) {
          navigate("/orders");
          toast.error(error.response?.data?.message || error.message);
        }
      }
    };

    fetchOrder();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate]);

  const deleteEntireOrder = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/orders");
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to do that");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const changeOrderStatus = async (e) => {
    e.preventDefault();
    let orderRoute;
    switch (userRole) {
      case "Administrator":
        orderRoute = "in_progress";
        break;
      case "Measurer":
        orderRoute = "complete";
        break;
      case "Cutter":
        orderRoute = "awaiting";
        break;
      case "Tailor":
        orderRoute = "assign-driver";
        break;
      case "Driver":
        orderRoute = "delivered";
        break;
      default:
        break;
    }

    if (
      userRole === "Administrator" &&
      orderStatusRef.current.value === "-- Select order status --"
    ) {
      toast.warn("Please select an order status");
      return;
    }

    if (userRole === "Measurer" && !cutter) {
      toast.warn("Please select a Cutter from the list");
      return;
    }

    if (userRole === "Cutter" && !tailor) {
      toast.warn("Please select a Tailor from the list");
      return;
    }

    if (userRole === "Tailor" && !driver) {
      toast.warn("Please select a Driver from the list");
      return;
    }

    try {
      const response = await axios.put(
        `${apiUrl}/orders/${order._id}/${orderRoute}`,
        {
          id: order._id,
          order_status: orderStatus,
          measurer,
          cutter,
          tailor,
          driver,
          comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setOrder(response.data.order);
      setOpenDialog(false);
      if (response.data.order.order_status === "Requires Alteration") {
        setOpenComment(true);
        toast.info("Make sure to add a comment");
      }
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to do that");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        toast.error("An error occurred: " + error.message);
      }
    }
  };

  const submitComment = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios
      .put(
        `${apiUrl}/orders/comments/${order._id}`,
        {
          comments: comments,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        setLoading(false);
        setOrder(response.data.order);
        setOpenComment(false);
      })
      .catch((error) => {
        console.error(error);
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="customer-details">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/orders"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> All Orders
            </Link>
          </div>
          {loading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(6).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[1.8rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-2/4 bg-[#666] animate-pulse m-auto h-[100vh] my-[2rem]"></div>
                </div>
              </div>
            </>
          )}
          {order && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => window.print()}
                >
                  Print <i className="fas fa-print"></i>
                </button>
                <form onSubmit={deleteEntireOrder}>
                  <button
                    type="submit"
                    className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  >
                    Delete <i className="fas fa-times"></i>
                  </button>
                </form>
                <button
                  type="submit"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[black] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => setOpenDialog(true)}
                >
                  Assign <i className="fas fa-user-plus"></i>
                </button>
                <button
                  type="submit"
                  className="py-[0.5rem] px-[1rem] outline-none border-none bg-[green] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                  onClick={() => setOpenComment(true)}
                >
                  Comment <i className="fas fa-message"></i>
                </button>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold flex items-center justify-center gap-4">
                Order Details{" "}
                <span
                  className={`p-1 text-lg rounded-xl ${applyAnimation(
                    order.order_status
                  )}`}
                >
                  {order.order_status}
                </span>
              </h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Measurements Taken By:</strong>
                  <p>{order.measurer || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Assigned By:</strong>
                  <p>{order.assigned_by || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Notes:</strong>
                  <p>{order.notes || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order No:</strong>
                  <p>{order.uniqueId}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Date Measurements Taken:</strong>
                  <p>
                    {new Date(order.order_start_time).toLocaleDateString(
                      "en-us",
                      { month: "long", year: "numeric", day: "numeric" }
                    )}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Customer ID No:</strong>
                  <Link
                    to={`/customers/${customer._id}`}
                    target="_blank"
                    className="text-[blue] no-underline hover:underline cursor-pointer"
                  >
                    {order.customer_id}
                  </Link>
                </span>
                {invoice ? (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Invoice No:</strong>
                    <Link
                      to={`/invoices/${invoice._id}`}
                      target="_blank"
                      className="text-[blue] no-underline hover:underline cursor-pointer"
                    >
                      {order.uniqueId} <i className="fas fa-external-link"></i>
                    </Link>
                  </span>
                ) : (
                  <span className="text-[red]">No invoice found</span>
                )}
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Total Fabric Quantity:</strong>
                  <p>
                    {order.details &&
                      order.details
                        .reduce((prev, curr) => prev + curr.quantity, 0)
                        .toFixed(2)}{" "}
                    m
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Tailoring Service Quantity:</strong>
                  <p>
                    {order.details &&
                      order.details.reduce(
                        (prev, curr) => prev + curr.quantity,
                        0
                      )}{" "}
                    x pieces
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Garment Types:</strong>
                  <p>
                    {order.details &&
                      order.details.map(
                        (item) => `${item.quantity} x ${item.name}, `
                      )}
                  </p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Number of Dependents:</strong>
                  <p>{order.dependents ? order.dependents.length : 0}</p>
                </span>
                <table>
                  <thead>
                    <tr>
                      <th>No</th>
                      <th>Fabric Name</th>
                      <th>Quantity</th>
                      <th>Tailoring Service</th>
                      <th>Measurements</th>
                    </tr>
                  </thead>
                  <tbody>
                    {order.details &&
                      order.details.map((item, index) => (
                        <tr key={index}>
                          <td>{index + 1}</td>
                          <td>{item.name}</td>
                          <td>{item.quantity}</td>
                          <td>{item.name}</td>
                          <td>
                            <Link
                              className="text-[blue] no-underline hover:underline cursor-pointer"
                              to={`/customers/${customer._id}/measurements`}
                              target="_blank"
                            >
                              Measurements{" "}
                              <i className="fas fa-external-link"></i>
                            </Link>
                          </td>
                        </tr>
                      ))}
                  </tbody>
                </table>
                <span className="flex flex-col justify-start gap-[2rem] my-[2rem] w-full">
                  <strong className="underline w-full">
                    Comments/Feedback:
                  </strong>
                  <div className="flex flex-col gap-[1rem]">
                    {order.comments &&
                      order.comments.map((comment) => (
                        <div className="bg-[#ddd] text-[1rem] p-[0.5rem] rounded-lg flex flex-col gap-2">
                          <span className="self-start text-khamco-secondary italic">
                            {comment.user}
                          </span>
                          <span>{comment.text}</span>
                          <span className="self-end">
                            <strong>
                              {formatDistanceToNow(
                                new Date(comment.timestamp),
                                { addSuffix: true }
                              )}
                            </strong>
                          </span>
                        </div>
                      ))}
                  </div>
                </span>
              </div>
            </div>
          )}
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openDialog}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={changeOrderStatus}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenDialog(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Update Order Details
                </h2>
              </div>
              {(userRole === "Driver" || userRole === "Administrator") && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Order Status{" "}
                    {order.order_status ? (
                      <span
                        className={`p-1 ${applyAnimation(order.order_status)}`}
                      >
                        {order.order_status}
                      </span>
                    ) : (
                      ""
                    )}
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setOrderStatus(e.target.value)}
                    ref={orderStatusRef}
                  >
                    <option
                      label="-- Select order status --"
                      value="-- Select order status --"
                    ></option>
                    <option value="Accepted" label="Accepted"></option>
                    <option value="In Progress" label="In Progress"></option>
                    <option value="Complete" label="Complete"></option>
                    <option
                      value="Awaiting Delivery"
                      label="Awaiting Delivery"
                    ></option>
                    <option value="Delivered" label="Delivered"></option>
                    <option
                      value="Requires Alteration"
                      label="Requires Alteration"
                    ></option>
                  </select>
                </div>
              )}
              {userRole === "Administrator" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label
                    className="text-lg my-2 select-none self-start font-medium"
                    htmlFor="measurer"
                  >
                    Measurer
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setMeasurer(e.target.value)}
                  >
                    <option label="-- Select the Measurer--" value=""></option>
                    {measurerUsers &&
                      measurerUsers.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Measurer" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    Cutter
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setCutter(e.target.value)}
                  >
                    <option label="-- Select the Cutter --" value=""></option>
                    {cutterUsers &&
                      cutterUsers.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Cutter" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    {" "}
                    Tailor
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setTailor(e.target.value)}
                  >
                    <option label="-- Select the Tailor--" value=""></option>
                    {tailorUsers &&
                      tailorUsers.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              {userRole === "Tailor" && (
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <label className="text-lg my-2 select-none self-start font-medium">
                    {" "}
                    Driver
                  </label>
                  <select
                    className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                    onChange={(e) => setDriver(e.target.value)}
                  >
                    <option label="-- Select the Driver--" value=""></option>
                    {driverUsers &&
                      driverUsers.map((user, index) => (
                        <option
                          key={index}
                          label={`${user.name} ${user.surname}`}
                          value={`${user.name} ${user.surname}`}
                        ></option>
                      ))}
                  </select>
                </div>
              )}
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Assign
                </button>
              </div>
            </form>
          </div>
        </dialog>
        <dialog
          className="fixed z-50 top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-screen md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openComment}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={submitComment}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenComment(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  Add a Comment
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label className="text-lg my-2 select-none self-start font-medium">
                  Comments
                </label>
                <textarea
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  placeholder="Write a comment"
                  onChange={(e) =>
                    setComments({
                      user: authorizedUser,
                      text: e.target.value,
                      timestamp: new Date(),
                    })
                  }
                ></textarea>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Update
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default OrderDetails;
