import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const Services = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/services"
    : "https://khamcotailor.com/services";
  const authKey = localStorage.getItem("jwt");
  const getProducts = async () => {
    await axios
      .get(`${serverUrl}/products`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setIsLoading(false);
        if (response.data.products) {
          setProducts(response.data.products);
        } else {
          setProducts([]);
        }
      })
      .catch((error) => {
        setMessage(error.message);
        setProducts([]);
        setIsLoading(false);
      });
  };

  const fetchServices = async (queryParams = "") => {
    try {
      const response = await axios.get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      const sortedServices = response.data.services.sort(
        (a, b) => new Date(a.date) - new Date(b.date)
      );
      setServices(sortedServices);
      setLoading(false);
    } catch (error) {
      setServices([]);
      setLoading(false);
      toast.error(error.message);
    }
  };

  useEffect(() => {
    document.title = "Services - Khamco Shop";
    fetchServices(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section className="py-0 px-[2rem] justify-center items-center grid-cols-1 gap-[1rem]">
          <h1 className="title">Services</h1>
          <div className="utilities">
            <Link to="/services/new">
              New Service <i className="fas fa-plus"></i>
            </Link>
          </div>
          <div className="utilities">
            <form onSubmit={handleSearch} className="search-engine">
              <input
                type="number"
                step={0.01}
                placeholder="Search service price"
                name="price"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form onSubmit={handleSearch} className="search-engine">
              <input
                type="date"
                name="date"
                id="date-search"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          {loading && (
            <table className="text-center">
              <thead>
                <tr>
                  <td>Loading...</td>
                </tr>
              </thead>
              <tbody>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
                <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                  <td>k</td>
                </tr>
              </tbody>
            </table>
          )}
          {services.length > 0 ? (
            <table className="w-full my-[2rem] m-auto border-collapse text-[1.2rem] overflow-auto">
              <thead className="text-khamco-primary font-bold">
                <tr className="border-[0.2rem] border-khamco-secondary border-t-0 relative">
                  <td className="bg-[#ddd] text-center">Service</td>
                  <td className="bg-[#ddd] text-center">Price</td>
                  <td className="bg-[#ddd] text-center">Date Created</td>
                  <td className="bg-[#ddd] text-center"></td>
                </tr>
              </thead>
              <tbody>
                {services.map((service) => (
                  <tr
                    key={service.id}
                    className="border-[0.2rem] border-khamco-secondary border-t-0 relative"
                  >
                    <td className="text-center p-[0.5rem]">
                      {service.service}
                    </td>
                    <td className="text-center p-[0.5rem]">
                      KWD {service.price}
                    </td>
                    <td className="text-center p-[0.5rem]">
                      {new Date(service.date).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })}
                    </td>
                    <td className="text-center p-[0.5rem]">
                      <Link to={`/services/${service._id}`}>View</Link>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p className="text-center">No services available.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Services;
