import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";

const AdminServiceTimes = () => {
  const [averageDurations, setAverageDurations] = useState([]);
  const [monthlyDeliveries, setMonthlyDeliveries] = useState(0);
  const [ordersInProgress, setOrdersInProgress] = useState([]);
  const [ordersAwaiting, setOrdersAwaiting] = useState([]);
  const [ordersCompleted, setOrdersCompleted] = useState([]);
  const [ordersDelivered, setOrdersDelivered] = useState([]);
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState("Employee Report");
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/service-times"
    : "https://khamco-shop-backend.onrender.com/admin/service-times";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Admin Services Data = Khamco Shop";
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setAverageDurations(response.data.averageDurations);
          setMonthlyDeliveries(response.data.monthly_deliveries);
          setOrdersInProgress(response.data.orders_inprogress);
          setOrdersAwaiting(response.data.orders_awaiting);
          setOrdersCompleted(response.data.orders_completed);
          setOrdersDelivered(response.data.orders_delivered);
          setUsers(response.data.users);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setIsLoading(true);
          setDisplay(false);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.message);
          }
          return;
        });
    };

    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, navigate]);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="admin-dashboard">
          <div className="utilities">
            <Link to="/orders" target="_blank">
              Orders <i className="fas fa-external-link"></i>
            </Link>
          </div>
          {isLoading && (
            <>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
                <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
                <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              </div>
              <table className="text-center">
                <thead>
                  <tr>
                    <td>Loading...</td>
                  </tr>
                </thead>
                <tbody>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                  <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                    <td>k</td>
                  </tr>
                </tbody>
              </table>
            </>
          )}
          {display && (
            <>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
                <div className="metric">
                  <p>Monthly Deliveries</p>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    {new Date(
                      `${
                        new Date().getMonth() + 1
                      }/01/${new Date().getFullYear()}`
                    ).toLocaleDateString("en-US", {
                      month: "long",
                      year: "numeric",
                    })}
                  </h2>
                  <h2 className="text-green-500 text-[2.5rem] no-underline">
                    {monthlyDeliveries}
                  </h2>
                </div>
                {ordersInProgress && (
                  <div className="metric">
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Orders In Progress
                    </h2>
                    <h2 className="text-[tomato] text-[2.5rem] no-underline">
                      {ordersInProgress.length}
                    </h2>
                  </div>
                )}
                {ordersCompleted && (
                  <div className="metric">
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Orders Completed
                    </h2>
                    <h2 className="text-[gold] text-[2.5rem] no-underline">
                      {ordersCompleted.length}
                    </h2>
                  </div>
                )}
                {ordersAwaiting && (
                  <div className="metric">
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Orders Awaiting Delivery
                    </h2>
                    <h2 className="text-orange-500 text-[2.5rem] no-underline">
                      {ordersAwaiting.length}
                    </h2>
                  </div>
                )}
                {ordersDelivered && (
                  <div className="metric">
                    <h2 className="text-khamco-secondary text-2xl font-bold">
                      Orders Delivered
                    </h2>
                    <h2 className="text-[lightgreen] text-[2.5rem] no-underline">
                      {ordersDelivered.length}
                    </h2>
                  </div>
                )}
              </div>
              <div className="search-engine mt-[4rem]">
                <select
                  className="search-input"
                  onChange={(e) => setSelectedIndex(e.target.value)}
                >
                  <option disabled>-- Select a Report --</option>
                  <option>Employee Report</option>
                  <option>Orders In Progress</option>
                  <option>Completed Orders</option>
                  <option>Orders Awaiting Delivery</option>
                  <option>Orders Delivered</option>
                </select>
              </div>
              {selectedIndex === "Employee Report" && (
                <table className="fadeIn">
                  <caption>Employee Report</caption>
                  <thead>
                    <tr>
                      <td>Name</td>
                      <td>Orders Cut</td>
                      <td>Orders Measured</td>
                      <td>Orders Sewn</td>
                    </tr>
                  </thead>
                  <tbody>
                    {users &&
                      users.map((user, index) => (
                        <tr key={index}>
                          <td>
                            {user.name} {user.surname}
                          </td>
                          <td>{user.cutting}</td>
                          <td>{user.measurements}</td>
                          <td>{user.sewing}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Orders In Progress" && (
                <table className="fadeIn">
                  <caption>
                    Service Report for Orders In Progress (Averages)
                  </caption>
                  <thead>
                    <tr>
                      <td>Month</td>
                      <td>Seconds</td>
                      <td>Minutes</td>
                      <td>Hours</td>
                      <td>Days</td>
                      <td>Weeks</td>
                      <td>Months</td>
                      <td>Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    {averageDurations &&
                      averageDurations.map((duration, index) => (
                        <tr key={index}>
                          <td>
                            {new Date(
                              `${duration._id}/01/${new Date().getFullYear()}`
                            ).toLocaleDateString("en-US", {
                              month: "long",
                              year: "numeric",
                            })}
                          </td>
                          <td>{duration.in_progress_seconds.toFixed(2)}</td>
                          <td>{duration.in_progress_minutes.toFixed(2)}</td>
                          <td>{duration.in_progress_hours.toFixed(2)}</td>
                          <td>{duration.in_progress_days.toFixed(2)}</td>
                          <td>{duration.in_progress_weeks.toFixed(2)}</td>
                          <td>{duration.in_progress_months.toFixed(2)}</td>
                          <td>{duration.in_progress_years.toFixed(2)}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Completed Orders" && (
                <table className="fadeIn">
                  <caption>
                    Service Report for Orders Completed (Averages)
                  </caption>
                  <thead>
                    <tr>
                      <td>Month</td>
                      <td>Seconds</td>
                      <td>Minutes</td>
                      <td>Hours</td>
                      <td>Days</td>
                      <td>Weeks</td>
                      <td>Months</td>
                      <td>Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    {averageDurations &&
                      averageDurations.map((duration, index) => (
                        <tr key={index}>
                          <td>
                            {new Date(
                              `${duration._id}/01/${new Date().getFullYear()}`
                            ).toLocaleDateString("en-US", {
                              month: "long",
                              year: "numeric",
                            })}
                          </td>
                          <td>{duration.completed_seconds.toFixed(2)}</td>
                          <td>{duration.completed_minutes.toFixed(2)}</td>
                          <td>{duration.completed_hours.toFixed(2)}</td>
                          <td>{duration.completed_days.toFixed(2)}</td>
                          <td>{duration.completed_weeks.toFixed(2)}</td>
                          <td>{duration.completed_months.toFixed(2)}</td>
                          <td>{duration.completed_years.toFixed(2)}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Orders Awaiting Delivery" && (
                <table className="fadeIn">
                  <caption>
                    Service Report for Orders Awaiting Delivery (Averages)
                  </caption>
                  <thead>
                    <tr>
                      <td>Month</td>
                      <td>Seconds</td>
                      <td>Minutes</td>
                      <td>Hours</td>
                      <td>Days</td>
                      <td>Weeks</td>
                      <td>Months</td>
                      <td>Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    {averageDurations &&
                      averageDurations.map((duration, index) => (
                        <tr key={index}>
                          <td>
                            {new Date(
                              `${duration._id}/01/${new Date().getFullYear()}`
                            ).toLocaleDateString("en-US", {
                              month: "long",
                              year: "numeric",
                            })}
                          </td>
                          <td>{duration.awaiting_seconds.toFixed(2)}</td>
                          <td>{duration.awaiting_minutes.toFixed(2)}</td>
                          <td>{duration.awaiting_hours.toFixed(2)}</td>
                          <td>{duration.awaiting_days.toFixed(2)}</td>
                          <td>{duration.awaiting_weeks.toFixed(2)}</td>
                          <td>{duration.awaiting_months.toFixed(2)}</td>
                          <td>{duration.awaiting_years.toFixed(2)}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
              {selectedIndex === "Orders Delivered" && (
                <table className="fadeIn">
                  <caption>
                    Service Report for Orders Delivered (Averages)
                  </caption>
                  <thead>
                    <tr>
                      <td>Month</td>
                      <td>Seconds</td>
                      <td>Minutes</td>
                      <td>Hours</td>
                      <td>Days</td>
                      <td>Weeks</td>
                      <td>Months</td>
                      <td>Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    {averageDurations &&
                      averageDurations.map((duration, index) => (
                        <tr key={index}>
                          <td>
                            {new Date(
                              `${duration._id}/01/${new Date().getFullYear()}`
                            ).toLocaleDateString("en-US", {
                              month: "long",
                              year: "numeric",
                            })}
                          </td>
                          <td>{duration.delivered_seconds.toFixed(2)}</td>
                          <td>{duration.delivered_minutes.toFixed(2)}</td>
                          <td>{duration.delivered_hours.toFixed(2)}</td>
                          <td>{duration.delivered_days.toFixed(2)}</td>
                          <td>{duration.delivered_weeks.toFixed(2)}</td>
                          <td>{duration.delivered_months.toFixed(2)}</td>
                          <td>{duration.delivered_years.toFixed(2)}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminServiceTimes;
