import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { Buffer } from "buffer";

const Products = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [products, setProducts] = useState([]);
  const serverUrl = "http://localhost:5000";

  useEffect(() => {
    const getProducts = async () => {
      await axios
        .get(`${serverUrl}/products`)
        .then((response) => {
          setProducts(response.data.products);
        })
        .catch((error) => {
          setMessage(error.message);
        });
    };

    getProducts();
  }, []);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          id="inventory"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products/new">
              Add New Product <i className="fas fa-cart-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <form action="/products" method="GET" className="search-engine">
              <input
                type="text"
                placeholder="Search product name"
                name="name"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              action="/products"
              method="GET"
              className="search-engine"
              id="date-form"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="title">All Products</h1>
          {products.length > 0 ? (
            products.map((product, index) => (
              <div
                className="flex items-center w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem]"
                dir="auto"
                key={index}
              >
                <img
                  src={`data:${product.photoType};base64, ${Buffer.from(photo.image.data).toString("base64")}`}
                  alt={product.name}
                  loading="lazy"
                  className="w-[15vw] h-[30vh]"
                />
                <p className="capitalize">
                  <strong>Name</strong>: {product.name}
                </p>
                <p className="capitalize">
                  <strong>Category</strong>: {product.category}
                </p>
                <Link
                  to={`/products/${product.id}`}
                  className="edit-btn transform-none"
                >
                  View
                </Link>
              </div>
            ))
          ) : (
            <p className="empty">No product data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Products;
