import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import { Link } from "react-router-dom";
import axios from "axios";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

const CustomersData = () => {
  const [message, setMessage] = useState("Hello World");
  const [militaryCustomers, setMilitaryCustomers] = useState([]);
  const [civilianCustomers, setCivilianCustomers] = useState([]);
  const [averageCollarSize, setAverageCollarSize] = useState(0);
  const [averageHeight, setHeight] = useState(0);
  const [popularAreas, setPopularAreas] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const apiURL = "http://localhost:5000/admin/customers_data";
  const chartRef = useRef(null);

  useEffect(() => {
    const fetchCustomerData = async () => {
      await axios
        .get(apiURL)
        .then((response) => {
          setCustomers(response.data.customers);
          setMilitaryCustomers(response.data.militaryCustomers);
          setCivilianCustomers(response.data.civilianCustomers);
          setMessage(response.data.message);
          //   setAverageCollarSize(
          //     customers.reduce(
          //       (previous, current) =>
          //         (previous + current.collar) / customers.length,
          //       0
          //     )
          //   );
          //   setHeight(
          //     customers.reduce(
          //       (previous, current) =>
          //         (previous + current.height) / customers.length,
          //       0
          //     )
          //   );
        })
        .catch((error) => {
          setMessage("There was a problem with the fetch operation.");
          console.error("There was a problem with the fetch operation:", error);
        });
    };

    fetchCustomerData();
  }, []);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chart = new Chart(ctx, {
      type: "scatter",
      data: {
        datasets: [
          {
            label: "Customers",
            data: customers.map((customer) => ({
              x: customer.height,
              y: customer.collar,
              name: `${customer.name} ${customer.surname}`,
            })),
            backgroundColor: "#283688",
            borderColor: "#EC1664",
            borderWidth: 1,
            pointRadius: 10,
          },
        ],
      },
      options: {
        scales: {
          y: {
            title: {
              display: true,
              text: "Height in Inches",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
          x: {
            title: {
              display: true,
              text: "Collar Diameter in Inches",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
        },
        plugins: {
          tooltip: {
            callbacks: {
              label: function (context) {
                return context.dataset.data[context.dataIndex].name;
              },
              afterLabel: function (context) {
                return `Height: ${context.parsed.x}, Collar: ${context.parsed.y}`;
              },
            },
          },
          legend: {
            display: true,
            labels: {
              color: "black",
              font: {
                size: 20,
              },
            },
          },
        },
      },
    });

    const chartElement = chartRef.current;

    // Event listener for click events on the chart
    const handleClick = (event) => {
      const clickedPoint = chart.getElementsAtEventForMode(
        event,
        "nearest",
        { intersect: true },
        true
      );
      if (clickedPoint.length > 0) {
        const clickedCustomerIndex = clickedPoint[0].index;
        const clickedCustomer = customers[clickedCustomerIndex];
        window.open(`/customers/${clickedCustomer._id}`, "_blank");
      }
    };

    chartElement.addEventListener("click", handleClick);

    return () => {
      chartElement.removeEventListener("click", handleClick);
      chart.destroy();
    };
  }, [customers]);

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          <p className="message">{message}</p>
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center">
            <div className="metric">
              <h2>Military Customers</h2>
              <h2 className="text-green-500">{militaryCustomers.length}</h2>
            </div>
            <div className="metric">
              <h2>Civilian Customers</h2>
              <h2 className="text-green-500">{civilianCustomers.length}</h2>
            </div>
            <div className="metric">
              <h2>Average Collar Size</h2>
              <p>Inches</p>
              <h2 className="text-green-500">
                {averageCollarSize.toFixed(2) || ""}
              </h2>
            </div>
            <div className="metric">
              <h2>Average Height</h2>
              <p>Inches</p>
              <h2 className="text-green-500">
                {averageHeight.toFixed(2) || ""}
              </h2>
            </div>
            <div className="metric">
              <h2>Popular Governorates</h2>
              <p>{popularAreas[0] && popularAreas[0].total} customers</p>
              <h2 className="text-green-500">
                {popularAreas[0] && popularAreas[0]._id}
              </h2>
            </div>
            <div className="metric">
              <h2>Popular Areas</h2>
              <p>
                {popularAreas[0] && popularAreas[0].areas[0].count} customers
              </p>
              <h2 className="text-green-500">
                {popularAreas[0] && popularAreas[0].areas[0].area}
              </h2>
            </div>
          </div>
          <div className="row search-engine">
            <select id="reportChoice">
              <option disabled>-- Select a report --</option>
              <option>Geography Report</option>
              <option>Customer Report</option>
              <option>Invoice Report</option>
              <option>Data Chart</option>
            </select>
          </div>
          <table>
            <caption>Geography Report</caption>
            <thead>
              <tr>
                <td>Governorate</td>
                <td>Country</td>
                <td>Total Customers</td>
              </tr>
            </thead>
            <tbody>
              {popularAreas &&
                popularAreas.map((area) => (
                  <tr>
                    <td>{area._id}</td>
                    <td>Kuwait</td>
                    <td>{area.total} customer(s)</td>
                  </tr>
                ))}
            </tbody>
          </table>
          <table id="customer-report">
            <caption>Customer Data</caption>
            <thead>
              <tr>
                <td>Customer</td>
                <td>Status</td>
                <td>Creation Date</td>
                <td>Membership</td>
                <td>Total Dependents</td>
                <td>Total Spent</td>
              </tr>
            </thead>
            <tbody>
              {customers &&
                customers.map((customer, index) => (
                  <tr key={index}>
                    <td>
                      <Link to={`/customers/${customer.id}`} target="_blank">
                        {customer.name} {customer.surname}{" "}
                        <i className="fas fa-external-link"></i>
                      </Link>
                    </td>
                    <td className="capitalize">{customer.status}</td>
                    <td>
                      {new Date(customer.date_created).toLocaleDateString(
                        "en-US",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                    </td>
                    {customer.membership_status === "VIP" ? (
                      <td>
                        {customer.membership_status}{" "}
                        <i className="fas fa-star blink"></i>
                      </td>
                    ) : (
                      <td>{customer.membership_status}</td>
                    )}
                    <td>{customer.dependents.length}</td>
                    <td>
                      KWD{" "}
                      {new Intl.NumberFormat("en-US").format(
                        customer.amount_spent
                      )}
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
          <table id="invoice-report">
            <caption>Recent Activity</caption>
            <thead>
              <tr>
                <td>Invoice ID</td>
                <td>Customer</td>
                <td>Date</td>
                <td>Contact</td>
                <td>Payment Status</td>
                <td>Amount Paid</td>
              </tr>
            </thead>
            <tbody>
              {invoices &&
                invoices.map((invoice) => (
                  <tr>
                    <td>
                      <a
                        href={`/invoices/${invoice.id}`}
                        target="_blank"
                        rel="noreferrer"
                      >
                        {invoice.uniqueId}{" "}
                        <i className="fas fa-external-link"></i>
                      </a>
                    </td>
                    <td>
                      {invoice.customer_name} {invoice.customer_surname}
                    </td>
                    <td>
                      {invoice.date.toLocaleDateString("en-US", {
                        month: "long",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </td>
                    <td>
                      <a href={`tel:${invoice.customer_phone}`}>
                        {invoice.customer_phone}
                      </a>
                    </td>
                    <td
                      className={`${
                        invoice.payment_status === "Paid"
                          ? "text-green-500"
                          : invoice.payment_status === "Partially Paid"
                          ? "text-orange-500"
                          : "text-red-500"
                      }`}
                    >
                      {invoice.payment_status}
                    </td>
                    <td>KWD {invoice.amount_paid}</td>
                  </tr>
                ))}
            </tbody>
          </table>
          <div className="row" id="data-chart">
            <canvas
              id="customerChart"
              width="400"
              height="150"
              ref={chartRef}
            ></canvas>
          </div>
        </section>
      </main>
      <Footer />
      <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    </>
  );
};

export default CustomersData;
