import { useState,  } from "react";
import { toast } from "react-toastify";

const Selector = ({ label, price, type, quantity, setOptions, options }) => {
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    const itemExists = options.some(item => item.name === label);
    if(itemExists){
      setIsChecked(true);
    }
  },[])

  const handleCheck = () => {
    if (quantity <= 0) {
      setIsChecked(false);
      return;
    }

    if (isChecked) {
      setOptions((prevArray) =>
        prevArray.filter((item) => item.name !== label)
      );
    } else {
      setOptions((prevArray) => {
        const exists = prevArray.some((item) => item.name === label);
        if (!exists) {
          return [
            ...prevArray,
            { name: label, price: price, quantity: 1, type },
          ];
        }
        return prevArray;
      });
    }
  };

  return (
    <label
      className={`p-2 w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 border-khamco-primary text-center font-medium ${
        isChecked ? "bg-[#ccc]" : ""
      }`}
      onClick={handleCheck}
    >
      <p className="text-sm">
        {label} - KWD {price}
      </p>
      <input
        type="checkbox"
        checked={isChecked}
        onChange={() => {
          if (quantity > 0) {
            setIsChecked(!isChecked);
          } else {
            toast.info(`${label} is finished`);
          }
        }}
        className="accent-khamco-primary"
      />
    </label>
  );
};

export default Selector;
