import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";

const MyOrders = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [orders, setOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/orders/my-orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setOrders(response.data.orders);
            setIsLoading(false);
            document.title = `Order Management - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, navigate, serverUrl]);

  return (
    <>
      <Navbar />
      <main>
        {isLoading && (
          <table className="text-center">
            <thead>
              <tr>
                <td>Loading...</td>
              </tr>
            </thead>
            <tbody>
              <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td>k</td>
              </tr>
              <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td>k</td>
              </tr>
              <tr className="bg-[#aaa] animate-pulse text-[#aaa] h-12">
                <td>k</td>
              </tr>
            </tbody>
          </table>
        )}
        <table>
          <thead>
            <tr>
              <td>Order ID</td>
              <td>Order Date</td>
            </tr>
          </thead>
          <tbody>
            {orders &&
              orders.map((order, index) => (
                <tr key={index}>
                  <td>{order._id}</td>
                  <td>
                    {new Date(order.date_created).toLocaleDateString("en-us", {
                      month: "long",
                      day: "numeric",
                      year: "numeric",
                    })}
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
        <Footer />
      </main>
    </>
  );
};

export default MyOrders;
