import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./Form_Fields";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const CreateCustomer = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const apiUrl = window.location.includes("localhost") ? "http://localhost:5000/customers" : "https://khamcotailer/customers";
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = "New Customer | Khamco Shop";
  }, []);

  const handleSubmit = async(event) =>{
    event.preventDefault();

    await axios.post(apiUrl, {
      headers: { Authorization: `Bearer ${authKey}` },
    }).then(response => {
      navigate("/customers/dependents/dependens-prompt")
    })
  }
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/customers">
              <i className="fas fa-arrow-left"></i> Customers
            </Link>
          </div>
          <h1 className="title">New Customer</h1>
          <p className="message">{message}</p>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={(e) => handleSubmit(e.target)}
          >
            <FormFields />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateCustomer;
