import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import UserForm from "./UserForm";
import Footer from "../../components/Footer";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const EditUser = () => {
  const { id } = useParams();
  const [checkMaster, setCheckMaster] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin/users/${id}`
    : `https://khamco-shop-backend.onrender.com/admin/users/${id}`;
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [language, setLanguage] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "Edit User Details - Khamco Shop";

    const fetchUserData = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setFirstName(response.data.user.name);
        setLastName(response.data.user.surname);
        setRole(response.data.user.role);
        setLanguage(response.data.user.language);
        setEmail(response.data.user.email);
      } catch (error) {
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        }
      }
    };

    fetchUserData();
  }, [apiUrl, authKey, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    await axios
      .put(
        apiUrl,
        {
          name: firstName,
          surname: lastName,
          email: email,
          role: role,
          language: language,
          password: password,
          password2: confirmPassword,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        setAuthorizedUser(response.data.authorizedUser);
        setCheckMaster(response.data.checkMaster);
        navigate(`/admin/users/${response.data.user._id}`);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        if (error.response) {
          if (error.response.status === 403) {
            toast.warn("You are not authorized");
          } else {
            toast.error(error.response.data?.message || error.message);
          }
        }
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section>
          <h1 className="title">Edit Tailor Details</h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <UserForm
              checkMaster={checkMaster}
              authorizedUser={authorizedUser}
              loading={loading}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setEmail={setEmail}
              setPassword={setPassword}
              setConfirmPassword={setConfirmPassword}
              firstName={firstName}
              lastName={lastName}
              role={role}
              language={language}
              email={email}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditUser;
