import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import AdminPanel from "../../../components/AdminPanel";
import Footer from "../../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import { Buffer } from "buffer";
import axios from "axios";
import { toast } from "react-toastify";
import ProductsForm from "../../products/ProductsForm";

const AdminProductsView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const [openDialog, setOpenDialog] = useState(false);
  const [display, setDisplay] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const authKey = localStorage.getItem("jwt");
  // Form fields
  const [productName, setProductName] = useState("");
  const [productQuantity, setProductQuantity] = useState("");
  const [productCategory, setProductCategory] = useState("");
  const [productPhoto, setProductPhoto] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productDate, setProductDate] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/admin/products/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          document.title = `Admin Product Details - ${response.data.product.name} - Khamco Shop`;
          setProduct(response.data.product);
          setProductName(response.data.product.name);
          setProductQuantity(response.data.product.quantity);
          setProductCategory(response.data.product.category);
          setProductPrice(response.data.product.price);
          setProductDate(response.data.product.date);
          setIsLoading(false);
          setDisplay(true);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error(error.response?.data?.message || error.message);
          }
          return;
        });
    };

    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, id, navigate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    await axios
      .put(
        `${apiUrl}/products/${id}`,
        {
          name: productName,
          quantity: productQuantity,
          category: productCategory,
          cover: productPhoto,
          price: productPrice,
          date: productDate,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        setOpenDialog(false);
        setProduct(response.data.product);
        setLoading(false);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          <div className="utilities">
            <Link to="/admin/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
          </div>
          {isLoading && <h1 className="title">Loading...</h1>}
          {display && (
            <div className="khamco-card">
              <div className="utilities">
                <button
                  className="edit-btn"
                  onClick={() => setOpenDialog(true)}
                >
                  Edit
                </button>
                <form
                  action="/products/<%= product.id %>?_method=DELETE"
                  method="POST"
                  className="d-inline"
                >
                  <button type="submit" className="delete-btn">
                    Delete
                  </button>
                </form>
              </div>
              {product && (
                <>
                  <h1 className="title">{product.name}</h1>
                  <img
                    src={`data:${product.photoType};base64, ${Buffer.from(
                      product.picture.data
                    ).toString("base64")}`}
                    alt={product.name}
                    loading="lazy"
                    className="w-[20vw] h-[40vh] m-auto"
                  />
                  <div className="flex flex-col gap-[1rem]">
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">ID</strong>
                      <p className="text-balance">{product._id}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">
                        Quantity
                      </strong>
                      <p className="text-balance">{product.quantity} meters</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Price</strong>
                      <p className="text-balance">
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(product.price)}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Cost</strong>
                      <p className="text-balance">
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(product.cost)}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">
                        Category
                      </strong>
                      <p className="text-balance capitalize">
                        {product.category}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="text-khamco-secondary">Date</strong>
                      <p className="text-balance">
                        {new Date(product.date).toLocaleDateString("en-us", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        })}
                      </p>
                    </span>
                  </div>
                </>
              )}
            </div>
          )}
        </section>
        <dialog
          id="editDialog"
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] p-[2rem] px-0 shadow-md shadow-[#333] fadeIn"
          open={openDialog}
        >
          <button
            className="delete-btn absolute top-[5%] left-[5%]"
            type="button"
            onClick={() => setOpenDialog(false)}
          >
            <i className="fas fa-times"></i>
          </button>
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={handleSubmit}
            >
              <ProductsForm
                setProductName={setProductName}
                setProductQuantity={setProductQuantity}
                setProductCategory={setProductCategory}
                setProductPhoto={setProductPhoto}
                setProductPrice={setProductPrice}
                setProductDate={setProductDate}
                productName={productName}
                productQuantity={productQuantity}
                productCategory={productCategory}
                productPhoto={productPhoto}
                productPrice={productPrice}
                productDate={productDate}
                loading={loading}
              />
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default AdminProductsView;
