import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import ProductsForm from "./ProductsForm";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const NewProduct = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [productName, setProductName] = useState("");
  const [productQuantity, setProductQuantity] = useState("");
  const [productCategory, setProductCategory] = useState("");
  const [productPhoto, setProductPhoto] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productDate, setProductDate] = useState("");
  const navigate = useNavigate();
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/products"
    : "https://khamco-shop-backend.onrender.com/products";
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "Enter Product Details Khamco Shop Home";
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append("name", productName);
    formData.append("quantity", productQuantity);
    formData.append("category", productCategory);
    formData.append("cover", productPhoto);
    formData.append("price", productPrice);
    formData.append("date", productDate);

    await axios
      .post(apiUrl, formData, {
        headers: { Authorization: `Bearer ${authKey}` },
        "Content-Type": "multipart/form-data",
      })
      .then((response) => {
        toast.success(response.data.message);
        navigate(`/products/${response.data.product._id}`);
        setLoading(false);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };
  
  return (
    <>
      <Navbar />
      <main className="p-16">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">Add New Product</h1>
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <ProductsForm
              setProductName={setProductName}
              setProductQuantity={setProductQuantity}
              setProductCategory={setProductCategory}
              setProductPhoto={setProductPhoto}
              setProductPrice={setProductPrice}
              setProductDate={setProductDate}
              date={productDate}
              loading={loading}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default NewProduct;
