import React, { useEffect, useState } from "react";

const UnitInput = ({ label, name, icon, unitType, onChange }) => {
  const [value, setValue] = useState(0);

  useEffect(() => {
    // Convert value when unitType changes
    if (unitType === "Inches") {
      setValue((prev) => (prev / 2.54).toFixed(2)); // Convert to inches
    } else {
      setValue((prev) => (prev * 2.54).toFixed(2)); // Convert to centimeters
    }
  }, [unitType]);

  const handleChange = (e) => {
    const newValue = parseFloat(e.target.value) || 0;
    setValue(newValue);
    onChange(name, newValue); // Call the parent's onChange handler
  };

  return (
    <div className="flex flex-col border-2 border-[black] my-[1rem]">
      <img src={icon || ""} alt={label} className="w-[12rem] m-auto" />
      <label className="text-center">
        {label} ({unitType === "Inches" ? "in" : "cm"})
      </label>
      <input
        className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
        type="number"
        step={0.1}
        name={name}
        placeholder={label}
        value={value}
        onChange={handleChange}
      />
    </div>
  );
};

export default UnitInput;
