import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";

const View = () => {
  const { id } = useParams();
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [product, setProduct] = useState({});
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/products"
    : "https://khamco-shop-backend.onrender.com/products";
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Product Details - Khamco Shop Home";

    const fetchProduct = async () => {
      try {
        const response = await axios.get(`${apiUrl}/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setProduct(response.data.product);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setProduct({});
        toast.error(error.response?.data?.message || error.message);
      }
    };

    fetchProduct();

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };

    determineWritingDirection();
  }, [apiUrl, authKey, id, userLanguage]);

  const renderImage = () => {
    if (product.picture && product.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(product.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${product.photoType};base64,${base64String}`}
          alt={product.name}
          loading="lazy"
          className="w-[20vw] h-[40vh] m-auto"
        />
      );
    }
    return null;
  };

  const handleDelete = async (productId) => {
    try {
      const response = await axios.delete(`${apiUrl}/${productId}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/products");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section dir={writingDirection}>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products">
              <i className="fas fa-arrow-left"></i> All Products
            </Link>
          </div>
          {loading && (
            <>
              <div className="khamco-card">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(2).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="khamco-card w-full bg-[#666] animate-pulse m-auto h-[100vh] my-[2rem]"></div>
                </div>
              </div>
            </>
          )}
          {product && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
                <Link to={`/products/edit/${product._id}`} className="edit-btn">
                  Edit
                </Link>
                <button
                  type="submit"
                  className="delete-btn"
                  onClick={() => handleDelete(product._id)}
                >
                  Delete
                </button>
              </div>
              <h1 className="title">{product.name}</h1>
              {renderImage()}
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>ID</strong>
                  <p>{product.id}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Quantity</strong>
                  <p>{product.quantity} meters</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Price</strong>
                  <p>KWD {product.price}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Category</strong>
                  <p>{product.category}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Date</strong>
                  <p className="capitalize">
                    {new Date(product.date).toLocaleDateString("en-us", {
                      month: "long",
                      year: "numeric",
                      day: "numeric",
                    })}
                  </p>
                </span>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default View;
