import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { Buffer } from "buffer";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";

const Products = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [products, setProducts] = useState([]);
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/products"
    : "https://khamco-shop-backend.onrender.com/products";
  const authKey = localStorage.getItem("jwt");
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();
  const {t} = useTranslation();

  const getProducts = async (queryParams = "") => {
    try {
      const response = await axios.get(`${serverUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setIsLoading(false);
      if (response.data.products) {
        setProducts(response.data.products);
      } else {
        setProducts([]);
      }
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
      setProducts([]);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Products Khamco Shop";
    getProducts(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section
          id="inventory"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products/new" className="text-[blue] no-underline hover:underline cursor-pointer">
              Add New Product <i className="fas fa-cart-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input
                type="text"
                placeholder="Search product name"
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form onSubmit={handleSearch} className="w-full md:w-2/5 flex justify-between mb-8">
              <input
                type="date"
                name="date"
                id="date-search"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button type="submit" className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">All Products ({products.length || 0})</h1>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[6rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                  dir={writingDirection}
                ></div>
              ))}
          {products.length > 0 &&
            products.map((product, index) => (
              <div
                key={index}
                className="flex items-center w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] my-[2rem]"
                dir="auto"
              >
                <img
                  src={`data:${product.photoType};base64, ${Buffer.from(
                    product.picture.data
                  ).toString("base64")}`}
                  alt={product.name}
                  loading="lazy"
                  className="w-[15vw] h-[30vh]"
                />
                <p className="capitalize">
                  <strong>Name</strong>: {product.name}
                </p>
                <p className="capitalize">
                  <strong>Category</strong>: {product.category}
                </p>
                <Link
                  to={`/products/${product._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 transform-none"
                >
                  View
                </Link>
              </div>
            ))}

          {products.length <= 0 && !isLoading && (
            <p className="text-center">No product data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Products;
