import { useTranslation } from "react-i18next";

const ProductsForm = ({
  setProductName,
  setProductQuantity,
  setProductCategory,
  setProductPhoto,
  setProductPrice,
  setProductDate,
  productName,
  productQuantity,
  productCategory,
  productPrice,
  productDate,
  loading
}) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };
  const {t} = useTranslation();

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">{t("Product Name")}</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="text"
          placeholder="Enter name of product"
          value={productName || undefined}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">Quantity</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="number"
          onChange={(e) => setProductQuantity(e.target.value)}
          value={productQuantity || undefined}
          placeholder="Product quantity"
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">Category</label>
        <select className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setProductCategory(e.target.value)}
          value={productCategory || undefined}
        >
          <option value="cotton">Cotton</option>
          <option value="linen">Linen</option>
          <option value="wool">Wool</option>
          <option value="polyester">Polyester</option>
          <option value="other">Other</option>
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">Product Photo</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="file"
          name="cover"
          onChange={(e) => setProductPhoto(e.target.files[0])}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">Price</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="number"
          step="0.01"
          placeholder="Product price"
          onChange={(e) => setProductPrice(e.target.value)}
          value={productPrice || undefined}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">Date</label>
        <input className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="date"
          onChange={(e) =>
            setProductDate(new Date(e.target.value).toISOString().split("T")[0])
          }
          value={productDate ? formatDate(productDate) : undefined}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button type="submit" className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer">{loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}</button>
      </div>
    </>
  );
};

export default ProductsForm;
