import axios from "axios";

const MeasurementList = ({ measurement }) => {
  const path = window.location.pathname;

  // Split the path to get individual segments
  const pathSegments = path.split('/');

  // Assuming the path is structured like /customers/:customerId/measurements/:measurementId
  const customerId = pathSegments[2]; // 3rd element
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${customerId}/measurements/${measurement._id}`
    : `https://khamco-shop-backend.onrender.com/customers/${customerId}/measurements/${measurement._id}`

  const changeUnits = async() => {
    const response = await axios.put(apiUrl, 
      {unit_type: unitType},
      {
        headers: { Authorization: `Bearer ${authKey}` },
      }
    )
  }
  return (
    <>
      <span className="gap-[1rem] flex">
        <strong className="w-1/4">Clothing Options: </strong>
        <div>
          {measurement.clothing_options &&
          measurement.clothing_options.length > 0 ? (
            measurement.clothing_options.map((item, index) => (
              <span key={index}> {item.clothing_item},</span>
            ))
          ) : (
            <p className="text-[red]">No clothing items selected.</p>
          )}
        </div>
      </span>
      {measurement.unit_type && (
        <span className="inline-flex justify-end items-center gap-[2rem] text-balance flex-wrap">
          <strong>Units: </strong>
          {measurement.unit_type}
          <button className="p-2 my-4 bg-khamco-secondary text-white rounded-md">Change</button>
        </span>
      )}
      {/* Dishdasha data */}
      {measurement.dishdasha_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Garment Length: </strong>
          {measurement.dishdasha_garment_length}
        </span>
      )}
      {measurement.dishdasha_front_height > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Front Height: </strong>
          {measurement.dishdasha_front_height}
        </span>
      )}
      {measurement.dishdasha_back_height > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Back Height: </strong>
          {measurement.dishdasha_back_height}
        </span>
      )}
      {/* Shirt data */}
      {measurement.shirt_type && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shirt Type: </strong>
          {measurement.shirt_type}
        </span>
      )}
      {measurement.shirt_sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shirt Sleeve: </strong>
          {measurement.shirt_sleeve}
        </span>
      )}
      {measurement.garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Garment Length: </strong>
          {measurement.garment_length}
        </span>
      )}
      {measurement.pants_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Pants Length: </strong>
          {measurement.pants_garment_length}
        </span>
      )}
      {measurement.shorts_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shorts Length: </strong>
          {measurement.shorts_garment_length}
        </span>
      )}
      {measurement.dress_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dress Length: </strong>
          {measurement.dress_garment_length}
        </span>
      )}
      {measurement.collar > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Collar: </strong>
          {measurement.collar}
        </span>
      )}
      {measurement.shoulders > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shoulders: </strong>
          {measurement.shoulders}
        </span>
      )}
