import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const OrderForm = ({
  loading,
  setMeasurer,
  setCutter,
  setTailor,
  setDriver,
  setOrderStatus,
  setComments,
  authorizedUser
}) => {
  const [measurerUsers, setMeasurerUsers] = useState([]);
  const [cutterUsers, setCutterUsers] = useState([]);
  const [tailorUsers, setTailorUsers] = useState([]);
  const [driverUsers, setDriverUsers] = useState([]);
  const devBaseUrl = "http://localhost:5000";
  const prodBaseUrl = "https://www.khamcotailor.com";
  const currentBaseUrl =
    window.location.hostname === "localhost" ? devBaseUrl : prodBaseUrl;
  const apiURL = `${currentBaseUrl}/api/customer-array`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    const getAllUsers = async () => {
      await axios
        .get(`${currentBaseUrl}/admin/tailors`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setMeasurerUsers(
            response.data.users.filter((user) => user.role === "Measurer")
          );
          setCutterUsers(
            response.data.users.filter((user) => user.role === "Cutter")
          );
          setTailorUsers(
            response.data.users.filter((user) => user.role === "Tailor")
          );
          setDriverUsers(
            response.data.users.filter((user) => user.role === "Driver")
          );
        })
        .catch((error) => {
          toast.error(error.response?.data?.message || error.message);
        });
    };

    getAllUsers();
  }, [apiURL, authKey, currentBaseUrl]);

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Measurer
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setMeasurer(e.target.value)}
        >
          <option label="-- Please select a user --"></option>
          {measurer.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Cutter
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setCutter(e.target.value)}
        >
          <option label="-- Please select a user --"></option>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Tailor
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setTailor(e.target.value)}
        >
          <option label="-- Please select a user --"></option>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Driver
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setDriver(e.target.value)}
        >
          <option label="-- Please select a user --"></option>
          {users.map((user, index) => (
            <option
              key={index}
              label={`${user.name} ${user.surname}`}
              value={`${user.name} ${user.surname}`}
            ></option>
          ))}
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Order Status
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setOrderStatus(e.target.value)}
        >
          <option label="-- Select order status --"></option>
          <option value="Accepted" label="Accepted"></option>
          <option value="In Progress" label="In Progress"></option>
          <option value="Complete" label="Complete"></option>
          <option value="Awaiting Delivery" label="Awaiting Delivery"></option>
          <option value="Delivered" label="Delivered"></option>
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label
          className="text-lg my-2 select-none self-start font-medium"
          htmlFor="order_start_time"
        >
          Order Start Date
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="datetime-local"
          name="order_start_time"
          id="order_start_time"
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Other Details
        </label>
        <textarea
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          placeholder="Comments"
          onChange={(e) =>
            setComments({
              user: authorizedUser,
              text: e.target.value,
              timestamp: new Date(),
            })
          }
        ></textarea>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          disabled={loading}
          className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
        >
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export default OrderForm;
