import React, { useState } from "react";

const Clothing = ({ label, imageUrl }) => {
  const [isChecked, setIsChecked] = useState(false);
  return (
    <div className={`hover:bg-[#ddd] flex flex-col items-center cursor-pointer rounded-xl p-2 ${isChecked ? "border-2 border-green-400 bg-[#ccc]" : ""
        }`}>
      <label
        htmlFor="clothing_item"
        className="w-28 flex flex-col gap-2 items-center cursor-pointer"
        onClick={(e) => e.preventDefault()}
      >
        <img src={imageUrl} alt={label} />
        <p className="text-sm">{label}</p>
      </label>
      <input
        type="checkbox"
        id="clothing_item"
        onChange={() => setIsChecked(!isChecked)}
        className="accent-orange-300"
      />
    </div>
  );
};

export default Clothing;
