import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const AdminTailorDetails = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/admin`
    : `https://khamco-shop-backend.onrender.com/admin`;
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const navigate = useNavigate();
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    let isMounted = true;

    const getUserDetails = async () => {
      await axios
        .get(`${apiUrl}/users/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setUser(response.data.user);
            document.title = `Admin Tailor Details - ${`${response.data.user.name} -`} Khamco Shop`;
            setIsLoading(false);
            setDisplay(true);
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(true);
            setDisplay(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
          }
          return;
        });
    };

    getUserDetails();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, navigate, user]);

  const deleteUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.delete(`${apiUrl}/tailors/${user._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section className="customer-details">
          {isLoading && (
            <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="h-[100vh] w-2/4 m-auto rounded-[2rem] bg-[#ccc] animate-pulse"></div>
            </div>
          )}
          {display && user && (
            <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="khamco-card w-full">
                <div className="utilities">
                  <Link
                    to={`/users/edit-user/${user._id}`}
                    className="edit-btn"
                  >
                    Edit
                  </Link>
                  <button
                    type="submit"
                    className="delete-btn"
                    onClick={deleteUser}
                  >
                    Delete
                  </button>
                </div>
                <h1 className="title">User Details {user.name}</h1>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">ID</strong>
                    <p className="capitalize">{user._id}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Name</strong>
                    <p className="capitalize">{user.name}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Surname</strong>
                    <p className="capitalize">{user.surname}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Role</strong>
                    <p className="capitalize">{user.role}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Language</strong>
                    <p className="capitalize">
                      {user.language === "en" && "English"}
                      {user.language === "ur" && "Urdu"}
                      {user.language === "ar" && "Arabic"}
                      {user.language === "hi" && "Hindi"}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong className="capitalize">Date Created</strong>
                    <p className="capitalize">
                      {new Date(user.date).toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })}
                    </p>
                  </span>
                </div>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
