import React, { useEffect, useState } from "react";
import Ribbon from "../../components/Ribbon";
import axios from "axios";
import { toast } from "react-toastify";

const ResetPassword = () => {
  const [passwordType, setPasswordType] = useState(false);
  const [password2Type, setPassword2Type] = useState(false);
  const [password, setPassword] = useState(false);
  const [password2, setPassword2] = useState(false);
  const urlParams = new URLSearchParams(window.location.search);
  const token = urlParams.get("token");
  const [loading, setLoading] = useState(false);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const baseUrl = window.location.origin.includes("localhost")
    ? "http://localhost:3000"
    : "https://khamcoshop";

  useEffect(() => {
    document.title = "Reset Password - Khamco Shop";

    const retrievedToken = async () => {
      try {
          if (token) {
              const response = await axios.get(
                  `${baseURL}/users/reset-password?token=${token}`
              );

              if (response.data.token) {
                  setResetPassword(true);
              }
          }
      } catch (error) {
          console.error(error);
      }
  };
  retrievedToken();
  }, []);

  const handleResetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post(
        `${apiUrl}/users/reset-password/${token}`,
        {
          password: password,
          password2: password2,
        }
      );
      setLoading(false);
      toast.success(response.data.message);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  return (
    <>
      <Ribbon />
      <main>
        <section className="p-[4rem] grid justify-center items-center">
          <h1 className="title">Change Password</h1>
          <form
            onSubmit={handleResetPassword}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="form-row">
              <label for="password">New Password</label>
              <input
                type={passwordType ? "text" : "password"}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter a password"
              />
              <button
                type="button"
                className="show-hide"
                onClick={() => setPasswordType(!passwordType)}
              >
                Show
              </button>
            </div>
            <div className="form-row">
              <label for="password2">Confirm Password</label>
              <input
                type={password2Type ? "text" : "password"}
                onChange={(e) => setPassword2(e.target.value)}
                placeholder="Enter a matching password"
              />
              <button
                type="button"
                className="show-hide"
                onClick={() => setPassword2Type(!password2Type)}
              >
                Show
              </button>
            </div>
            <div className="form-row">
              <button type="submit">
                {loading ? (
                  <i className="fas fa-spinner animate-spin"></i>
                ) : (
                  "Reset Password"
                )}
              </button>
            </div>
          </form>
        </section>
      </main>
    </>
  );
};

export default ResetPassword;
