export const hasTop = (list) => {
  return (
    list.includes("Dish Dasha") ||
    list.includes("T-Shirt") ||
    list.includes("Girl T-Shirt") ||
    list.includes("Polo Shirt") ||
    list.includes("Shirt") ||
    list.includes("Long Shirt") ||
    list.includes("Jacket") ||
    list.includes("Girl Hoodie") ||
    list.includes("Suit") ||
    list.includes("Dress") ||
    list.includes("Tank Top") ||
    list.includes("Bra")
  );
};

export const hasBottom = () => {
    return (
      clothingOptions.includes("Dish Dasha") ||
      clothingOptions.includes("Pants") ||
      clothingOptions.includes("Shorts") ||
      clothingOptions.includes("Ragged Shorts") ||
      clothingOptions.includes("Suit") ||
      clothingOptions.includes("Dress") ||
      clothingOptions.includes("Briefs")
    );
  };

  export const hasHeadWear = () => {
    return (
      clothingOptions.includes("Beanie") || clothingOptions.includes("Snapback")
    );
  };

  export const isVest = () => {
    return clothingOptions.includes("Tank Top");
  };

  export const isFootWear = () => {
    return (
      clothingOptions.includes("Vans") ||
      clothingOptions.includes("Flip Flops") ||
      clothingOptions.includes("Shoes") ||
      clothingOptions.includes("Heels") ||
      clothingOptions.includes("Socks")
    );
  };
