import React from "react";

const MeasurementList = ({ measurement }) => {
  return (
    <>
      <span className="gap-[0.1rem] flex">
        <strong className="w-2/4">Clothing Options: </strong>
        <div>
          {measurement.clothing_options &&
          measurement.clothing_options.length > 0 ? (
            measurement.clothing_options.map((item) => (
              <span> ({item.clothing_item},)</span>
            ))
          ) : (
            <p className="text-[red]">No clothing items selected.</p>
          )}
        </div>
      </span>
      {measurement.waist > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Waist</strong>
          {`${measurement.waist} inches.`}
        </span>
      )}
      {measurement.natural_waist > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Natural Waist</strong>
          {`${measurement.natural_waist} inches.`}
        </span>
      )}
      {measurement.garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Garment Length</strong>
          {`${measurement.garment_length} inches.`}
        </span>
      )}
      {measurement.half_across > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Half Across</strong>
          {`${measurement.half_across} inches.`}
        </span>
      )}
      {measurement.chest > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Chest</strong>
          {`${measurement.chest} inches.`}
        </span>
      )}
      {measurement.trouser_waist_position > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Trouser Waist Position</strong>
          {`${measurement.trouser_waist_position} inches.`}
        </span>
      )}
      {measurement.seat > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Seat</strong>
          {`${measurement.seat} inches.`}
        </span>
      )}
      {measurement.neck > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Neck</strong>
          {`${measurement.neck} inches.`}
        </span>
      )}
      {measurement.shoulder > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shoulder</strong>
          {`${measurement.shoulder} inches.`}
        </span>
      )}
      {measurement.sleeve_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Sleeve Length</strong>
          {`${measurement.sleeve_length} inches.`}
        </span>
      )}
      {measurement.side_seam_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Side Seam Length</strong>
          {`${measurement.side_seam_length} inches.`}
        </span>
      )}
      {measurement.body_rise > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Body Rise</strong>
          {`${measurement.body_rise} inches.`}
        </span>
      )}
      {measurement.inside_leg > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Inside Leg</strong>
          {`${measurement.inside_leg} inches.`}
        </span>
      )}
      {measurement.wrist > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Wrist</strong>
          {`${measurement.wrist} inches.`}
        </span>
      )}
      {measurement.top_arm > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Top Arm</strong>
          {`${measurement.top_arm} inches.`}
        </span>
      )}
      {measurement.scye_depth > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Scye Depth</strong>
          {`${measurement.scye_depth} inches.`}
        </span>
      )}
      {measurement.arm_sleeve_hole > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Arm Sleeve Hole</strong>
          {`${measurement.arm_sleeve_hole} inches.`}
        </span>
      )}
      {measurement.frontal_collar > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Frontal Collar</strong>
          {`${measurement.frontal_collar} inches.`}
        </span>
      )}
      {measurement.hip > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Hip</strong>
          {`${measurement.hip} inches.`}
        </span>
      )}
      {measurement.front_rise > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Front Rise</strong>
          {`${measurement.front_rise} inches.`}
        </span>
      )}
      {measurement.back_rise > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Back Rise</strong>
          {`${measurement.back_rise} inches`}
        </span>
      )}
      {measurement.thigh > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Thigh</strong>
          {`${measurement.thigh} inches`}
        </span>
      )}
      {measurement.knee > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Knee</strong>
          {`${measurement.knee} inches`}
        </span>
      )}
      {measurement.hem > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Hem</strong>
          {`${measurement.hem} inches.`}
        </span>
      )}
      {measurement.outseam > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Outseam</strong>
          {`${measurement.outseam} inches.`}
        </span>
      )}
      {measurement.foot_size > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Foot Size</strong>
          {measurement.foot_size}
        </span>
      )}
      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
        <strong>Head Size</strong>
        {measurement.head_size > 0 ? 
        measurement.head_size : "--"}
      </span>
      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
        <strong>Description/Notes:</strong>
        {measurement.description ? <p>{measurement.description}</p> : "--"}
      </span>
    </>
  );
};

export default MeasurementList;
