import React, { useState } from "react";
import Navbar from "../../../components/Navbar";
import AdminPanel from "../../../components/AdminPanel";
import Footer from "../../../components/Footer";
import { Link } from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const AdminProducts = () => {
  const [message, setMessage] = useState("");
  const [products, setProducts] = useState([]);
  const apiUrl = "http://localhost:5000/admin/products";
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(apiUrl)
        .then((response) => {
          setDisplay(true);
          setIsLoading(false);
          console.log(response.data);
        })
        .catch((error) => {
          setDisplay(false);
          setIsLoading(true);
          setMessage(error.message);
        });
    };

    return () => {
      fetchData();
    };
  }, []);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section id="inventory" className="khamco-section">
          <div className="utilities">
            <Link to="/products/new">
              Add New Product <i className="fas fa-cart-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          {isLoading && <h1 className=""></h1>}
          {display && (
            <>
              <div className="utilities">
                <form action="/products" method="GET" className="search-engine">
                  <input
                    type="text"
                    placeholder="Search product name"
                    name="name"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-search"></i>
                  </button>
                </form>
                <form
                  action="/products"
                  method="GET"
                  className="search-engine"
                  id="date-form"
                >
                  <input
                    type="date"
                    name="date"
                    id="date-search"
                    className="search-input"
                  />
                  <button type="submit" className="search-button">
                    <i className="fas fa-check"></i>
                  </button>
                </form>
              </div>
              <h1 className="title">All Products</h1>
              {products &&
                products.map((product) => (
                  <div className="flex-wrapper">
                    <img
                      src="<%= product.photoPath %>"
                      alt="The associated product"
                      loading="lazy"
                    />
                    <p>
                      <strong>Name</strong>: {product.name}
                    </p>
                    <p>
                      <strong>Category</strong>: {product.category}
                    </p>
                    <Link to={`/admin/products/${product.id}`}>
                      View Product
                    </Link>
                  </div>
                ))}
              {!products && <p className="empty">No product data.</p>}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminProducts;
